function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);

	if(ds_cond.GetRowCount() < 1) ds_cond.AddRow();

	// 작성 기간
	cal_fdt.Value = substr(gfn_getCurrDate(),0,4) + "0101";
	cal_tdt.Value = gfn_getCurrDate();

	// if(G_USER_ID == "2017938"  //최인식
	// || G_USER_ID == "2021056" || G_USER_ID == "2021153"  //전현정 임유진
	// || G_USER_ID == "2017750" || G_USER_ID == "2021200" || G_USER_ID == "2018900" || G_USER_ID == "2029758" || G_USER_ID == "2999999"  //이주형 이하림
	// || G_USER_ID == "2019175" || G_USER_ID == "2018870"  //박준성 
	// || G_USER_ID == "2023466" || G_USER_ID == "2023687") { //이경해 용아름
		// ufn_enableBtn("approval", true);
	// } else {
		// ufn_enableBtn("approval", false);
	// }
	//==============승인 버튼 권한==============	
	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS1242001A");
	ds_rcond.SetColumn(0, "GBN", "01");	
	pdchang_visible = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();

	if ( pdchang_visible == true) {	
		ufn_enableBtn("approval", true);
	} else {
		ufn_enableBtn("approval", false);
	}
	//==========================================
}

function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_pst.Index = 0;
	cb_jpgbn.Index = 0;

	ed_upn.Enable = false;
	ed_cst.Enable = false;
}

