	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list_chk=ds_list"
        , ""
        , ""
		, false
        , false
        , true);

}

/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() {
	gfn_clearMsgBar();

	if(ds_list.rowCount() < 1) {
		// 삭제할 정보 없음
		return;
	}

	ds_list3.ClearData();

	var j = 0;
	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) { //체크박스가 true 일 경우
			ds_list3.AddRow();
			ds_list3.SetColumn(j, "FLAG", "U");
			ds_list3.SetColumn(j, "GNO", ds_list.GetColumn(i, "GNO"));
			ds_list3.SetColumn(j, "HIS", ds_list.GetColumn(i, "HIS"));

			j++;
		}
	}

	if(j == 0) {
		gfn_showMsg("CW00005");
		return;		
	}

	tit_clearActionInfo();
	tit_addSaveActionInfo(
        ""
        , "cs12:CS1242001A_U4"
        , ""
        , "FLAG");

	tit_callService(
        ""
        , ""
        , "ds_list=ds_list3"
        , ""
        , ""
        , "fn_afterDelete");

}

