/********************************************************************************
* 기      능   : 그리드 셀 더블 클릭 시 이벤트
********************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	var vHead = grd_list.GetCellProp("HEAD",nCell,"text");
	if (vHead == "비고") {						//if(nCell == 19) {
		var args = "gno=" + quote(ds_list.GetColumn(nRow,"GNO"))
				+ " his=" + quote(ds_list.GetColumn(nRow,"HIS"))
				+ " upn='' cst=''" 
				+ " frm='B'";

		gfn_openPopup("CS1242001B", true, args);
	} else if (vHead == "결재문서번호") {				//if(nCell == 17) {
		var args = "fa_docno=" + quote(ds_list.GetColumn(nRow,"DOCNO"))
				 + " fa_gno=" + quote(ds_list.GetColumn(nRow,"GNO"))
				 ;

		gfn_openPopup("CS1242001D", true, args);		

	} else if (vHead == "결재진행상태") {				//iif(nCell == 18) {
		var args = "fa_docno=" + quote(ds_list.GetColumn(nRow,"DOCNO"))
				 + " fa_gno=" + quote(ds_list.GetColumn(nRow,"GNO"))
				 ;

		gfn_openPopup("CS0405001A", true, args);		
	}
}

