/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {
	gfn_clearMsgBar();

    if(ds_list.FindRow("CHK", 1) == -1) {
		gfn_showMsg("CW00013");
		return;
	}

	ds_list2.ClearData();

	var j = 0;
	for(var i=0; i<ds_list.RowCount(); i++) {					
		if(ds_list.GetColumn(i, "CHK") == true) {
			// if(length(trim(ds_list.GetColumn(i, "SDT"))) == 0) {
				// gfn_showAlert("설치일을 입력하십시요.");
				// return;
			// }

			// if(length(trim(ds_list.GetColumn(i, "ETP"))) == 0) {
				// gfn_showAlert("교환기타입을 선택하십시요.");
				// return;
			// }

			// if(length(trim(ds_list.GetColumn(i, "CMD"))) == 0) {
				// gfn_showAlert("통신방식을 선택하십시요.");
				// return;
			// }
/*
			if(length(trim(ds_list.GetColumn(i, "SFN"))) == 0) {
				gfn_showAlert("설치완료여부를 체크하십시요.");
				return;
			}
*/
			// if(length(trim(ds_list.GetColumn(i, "TL1"))) == 0) {
				// gfn_showAlert("동을 입력하십시요.");
				// return;
			// }

			// if(length(trim(ds_list.GetColumn(i, "TL2"))) == 0) {
				// gfn_showAlert("호기를 입력하십시요.");
				// return;
			// }

			// if(length(trim(ds_list.GetColumn(i, "TL3"))) == 0) {
				// gfn_showAlert("전화번호를 입력하십시요.");
				// return;
			// }

			// if(length(trim(ds_list.GetColumn(i, "EOE"))) == 0) {
				// gfn_showAlert("설치업체를 입력하십시요.");
				// return;
			// }

			// if(length(trim(ds_list.GetColumn(i, "EPN"))) == 0) {
				// gfn_showAlert("설치작업자를 입력하십시요.");
				// return;
			// }

			ds_list2.AddRow();
			ds_list2.SetColumn(j, "FLAG", "U");
			// ds_list2.SetColumn(j, "UPN", ds_list.GetColumn(i, "UPN"));
			// ds_list2.SetColumn(j, "CST", ds_list.GetColumn(i, "CST"));
			ds_list2.SetColumn(j, "PJT", ds_list.GetColumn(i, "PJT"));
			ds_list2.SetColumn(j, "HNO", ds_list.GetColumn(i, "HNO"));
			// ds_list2.SetColumn(j, "GNO", ds_list.GetColumn(i, "GNO"));
			// ds_list2.SetColumn(j, "SDT", ds_list.GetColumn(i, "SDT"));
			// ds_list2.SetColumn(j, "ETP", ds_list.GetColumn(i, "ETP"));
			// ds_list2.SetColumn(j, "CMD", ds_list.GetColumn(i, "CMD"));
			ds_list2.SetColumn(j, "SFN", ds_list.GetColumn(i, "SFN"));
			// ds_list2.SetColumn(j, "TL1", ds_list.GetColumn(i, "TL1"));
			// ds_list2.SetColumn(j, "TL2", ds_list.GetColumn(i, "TL2"));
			// ds_list2.SetColumn(j, "TL3", ds_list.GetColumn(i, "TL3"));
			// ds_list2.SetColumn(j, "EOE", ds_list.GetColumn(i, "EOE"));
			// ds_list2.SetColumn(j, "EPN", ds_list.GetColumn(i, "EPN"));
			ds_list2.SetColumn(j, "RMK", ds_list.GetColumn(i, "RMK"));
			ds_list2.SetColumn(j, "TL4", ds_list.GetColumn(i, "TL4"));
			ds_list2.SetColumn(j, "TL5", ds_list.GetColumn(i, "TL5"));

			j++;
		}
	}

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""
//		, "cs12:CS1243001A_I1"
		, "cs12:CS1243001A_U1"
		, ""
		, "FLAG");
	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2"
		, ""
		, ""
		, "fn_afterSave"
		, true);
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00005");
		fn_query();
	}
}

