	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

/*
	var firstDay = substr(gfn_getCurrDate(),0,6) + "01";
	cal_fm.value = "";
	cal_to.value = "";
*/	
	cal_fm.value = gfn_getBeforeMonth(substr(gfn_getCurrDate(),0,6),11) + '01';
	cal_to.value = gfn_getCurrDate();



}

