#include "LIB::commons.js";	// 공통 스크립트 
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================

//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	var curYear = substr(gfn_getCurrDate(),0,4);
	fn_makeYear(curYear,-3,10);
	cb_month.index = parseInt(substr(gfn_getCurrDate(),4,2));
	cb_year.SetFocus();

	cb_gbn.Index = 0;
	cal_dat.Value = gfn_getCurrDate();
}

/************************************************************************************
* 기      능   : 작업기간 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
************************************************************************************/
function fn_makeYear(curYear,sNum,eNum) {
	var rowCnt = eNum-sNum;
	var row = 1;

	for(var i=rowCnt; i>-1; i--) {
		ds_year_cd.AddRow();
		ds_year_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_year_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		row++;
	}

	cb_year.index = eNum + 1;
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {

}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {
	var user_grp = gfn_getUserGroup();

	if(user_grp == '40') {
		if(gfn_isNull(div_code.cb_big, true, st_bsu.value))
			return;
	}

	if(gfn_isNull(cal_dat, true, st_dat.value)) {
		return;
	}

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		}
	}

	var fYear  = "";
	var fMonth = "";

	if(length(trim(cb_year.value)) > 0) {
		fYear = parseInt(cb_year.value);
	}

	if(length(trim(cb_month.value)) > 0) {
		fMonth = parseInt(cb_month.value);
	}

	if(length(trim(fYear)) == 0 && length(trim(fMonth)) == 0) {
		ds_cond.SetColumn(0,"SEL",'');
	}

	if(length(trim(fYear)) == 0 && length(trim(fMonth)) > 0) {
		ds_cond.SetColumn(0,"SEL",'A');
	}

	if(length(trim(fYear)) > 0 && length(trim(fMonth)) == 0) {
		ds_cond.SetColumn(0,"SEL",'B');
	}

	if(length(trim(fYear)) > 0 && length(trim(fMonth)) > 0) {
		ds_cond.SetColumn(0,"SEL",'C');
	}

	ds_cond.SetColumn(0,"YYMM",ds_cond.GetColumn(0,"YYD")+ds_cond.GetColumn(0,"MMD"));

	ds_cond.SetColumn(0, "PJT", ed_pjt.Value);
	ds_cond.SetColumn(0, "GBN", cb_gbn.Value);
	ds_cond.SetColumn(0, "DAT", cal_dat.Value);
	ds_cond.SetColumn(0, "DAT1", gfn_getLeapLastDay(gfn_getBeforeMonth(substr(cal_dat.Value,0,6), 1) + "01"));

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs12:CS1250001A_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002", ds_list.RowCount()+"");
}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================

/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	if(nCell == 0) {
		// 전체 선택
		gfn_selectAllGrid2(obj,0,0,true);
	} else {
		// 그리드 정렬 처리
		gfn_sortGrid(obj,nCell);
	}
}

/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel() {
	gfn_saveToExcel(grd_list, true);
}

function btn_pjt_OnClick(obj) {
	var arg = "pjt="+quote(ed_pjt.Text);
	gfn_openPopup("CS9601002A",true,arg);
}

function btn_write_OnClick(obj) {
	gfn_clearMsgBar();

	if(ds_list.FindRow("CHK", 1) == -1) {
		gfn_showMsg("CW00013");
		return;
	}

	var arg = "lnk=INS";
	gfn_openPopup("CS1250001B",true,arg);
}

function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	if(nCell <> 0) {
		if(length(trim(ds_list.GetColumn(nRow, "ETN"))) > 0) {
			var arg = "lnk=UPD";
			gfn_openPopup("CS1250001B",true,arg);
		}
	}
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}
