/********************************************************************************
* 기      능   : 출력
********************************************************************************/
function fn_print() {
	gfn_clearMsgBar();

	if(ds_list.rowCount() < 1) {
		// 출력할 정보 없음
		return;
	}

	if(!gfn_isChangeDs(ds_list, true)) {
		return;
	}

	var k = 0;
	var z = 0;
	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) { //체크박스가 true 일 경우
			if(ds_list.GetColumn(i, "PST") <> "A6") {
				var arr = [parseInt(i) + 1];
				gfn_showMsg("CW00042", arr);
				return false;
			}

			z = i;
			k++;
		}
	}

	if(k == 0 || k > 1) {
		gfn_showMsg("견적서 출력은 한건씩 가능합니다.");
		return false;
	}

	ds_condP.SetColumn(0, "ETN", ds_list.GetColumn(z, "ETN"));
	ds_condP.SetColumn(0, "SIR", ds_list.GetColumn(z, "SIR"));

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs12:CS1251001A_S2");
	tit_callService(
		""
		, ""
		, "ds_condP=ds_condP"
		, "ds_listP=ds_listP"
		, ""
		, "fn_afterPrint"
		, true
		, true);
}

