/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	tit_clearActionInfo();

	// 띄우는 화면에 따라 조회쿼리가 다름
	if(F_PFORM=="CS1302") {
		tit_addSearchActionInfo("cs13:CS1301001B_S15");
	} else {
		tit_addSearchActionInfo("cs13:CS1301001B_S11");
	}

	var args = "F_UPN="+quote(F_UPN);
		args += " F_CST="+quote(F_CST);
		args += " F_SEQ="+quote(F_SEQ);

	tit_callService(
        ""
        , ""
        , ""
        , "ds_list2=ds_list"
        , args
        , "fn_afterQuery"
        , true);
}
