/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( ds_error.rowcount() > 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");		

		// a.7 현장위치정보() update
		if(ds_master.GetColumn(0, "CS142_PST")=="A6"){

			 // CS용으로 변환
			 // tit_setBiz(G_CS_BIZ);   // => CS_BIZ_XA
			 tit_clearActionInfo();

			 // SMS 문자메세지 정보 insert(em_tran) 
			 tit_addSingleActionInfo("cs13:CS1301001B_U5");

			 // 다시 초기화
			// tit_clearBiz();

			 tit_callService(
				"CS_ACT"
				, ""
				, "ds_detail=ds_detail"
				, ""
				, ""
				, "fn_afterSave2"
				, true);	


		} else {
			ds_master.DeleteAll();
			ds_detail.DeleteAll();
			ds_list3.DeleteAll();
			fn_query();
		}
	}


}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave2(errCode, errMsg) {

	if ( ds_error.rowcount() > 0 ) {
		//gfn_showMsg("CE00001");
	} else {
		//gfn_showMsg("CI00003");	
	}

