/********************************************************************************
* 기      능   : master / detail 데이터셋 초기 데이터 셋팅 
********************************************************************************/
function ufn_checkPerRow(gbn, rows){
	var uGroup = gfn_getUserGroup();
	var curPst = ds_list2.getColumn(rows, "CS142_PST");

	if(curPst == "A6"){
		gfn_showMsg("CW00121", "승인");
		//trace("승인 호기 존재");
		return false;
	}
	// 협력사, 지방사무소 (delete, save, trans)
	if(uGroup == "10" || uGroup == "30"){
		if(curPst=="A3"||curPst=="A5"){
			gfn_showMsg("CW00121", "발송 또는 전송 ");
			//trace("이미 발송, 전송 호기 존재");
			return false;
		}
	// PM (receipt, sendback)	
	} else if(uGroup == "20") {
		if(curPst!="A3" && curPst!="A5"){
			gfn_showMsg("CW00122", "발송 또는 전송 ");
			//trace("발송,전송 이외의 넘들이 있슴");
			return false;
		}
	// 고객지원부 (approval,sendback)
	} else if(uGroup == "40") {
		if(curPst!="A5"){
			gfn_showMsg("CW00122", "접수");
			//trace("접수 이외의 항목 존재");
			return false;
		}		
	}
	return true;
}
function ufn_save(gbn){
	if(ds_List2.FindRow("CHECK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}

	for(var k=0; k<ds_List2.GetRowCount(); k++){
		if(ds_List2.getColumn(k, "CHECK")==1){
			if(ds_List2.GetColumn(k, "GBN_CODE")!="D"){
				gfn_showMsg('CW00093');
				return;
			}
			if(length(ds_List2.GetColumn(k, "FSD"))>0){
				gfn_showMsg('CW00094');
				return;
			}

			if(!ufn_checkPerRow(gbn, k)){
				return;
			}
		}

	}
	if(gfn_isNull(cb_statusG, true, "실패구분")){
		return;
	}
	if(gfn_isNull(cal_fail_dt, true, "실패일자")){
		return;
	}

	// 2009.08.04 LJH 실패월이 현재월보다 2개월전인 데이터는 입력 불가능 하도록
	if(substr(cal_fail_dt.value,0,6) <= gfn_getBeforeMonth(substr(gfn_getCurrDate(),0,6),2)) {
		gfn_showMsg("CW00008", Static11.Text);
		return;
	}

	// 작성 & 미작성 상태의 호기가 같이 있을때 confirm popup (20080602)	
	if(gbn=="A3"){
		if(ds_list2.FindRow("CS142_PST", "A1")!=-1){
		var cntA0=0;
			for(var v=0; v<ds_list2.GetRowCount(); v++){
				if(length(ds_list2.getColumn(v, "CS142_PST"))<=1 || ds_list2.getColumn(v, "CS142_PST")==""){
					cntA0++;
				}			
			}
			if(cntA0 > 0){
				if(!gfn_showConfirm( "CW00119", cntA0 )){
					return;
				}
			}			
		}
	}

	var row = ds_list2.row;
	gfn_initDs(ds_master, true);
	var uGroup = gfn_getUserGroup();

	// ds_master setting
	if(gbn=='A1'){		
		ds_master.SetColumn(0, "CS141_FBD", "");	// 실패발송일자
		ds_master.SetColumn(0, "CS141_FBJ", "");	// 실패발송자
	} else {		
		ds_master.SetColumn(0, "CS141_FBD", gfn_getCurrDate());	// 실패발송일자
		ds_master.SetColumn(0, "CS141_FBJ", G_USER_ID);	// 실패발송자
	}

	ds_master.SetColumn(0, "ISEXIST", "N");
	ds_master.SetColumn(0, "MANDT", G_MANDT);	// MANDT
	ds_master.SetColumn(0, "CS141_PST", gbn);	// 진행상태
	ds_master.SetColumn(0, "CS141_GBN", cb_statusG.Value);	// 실패구분
	ds_master.SetColumn(0, "CS141_JHD", cal_fail_dt.Value);	// 실패일자		
	ds_master.SetColumn(0, "CS141_UPN", ds_list2.GetColumn(row, "CS122_UPN"));	// proj no 
	ds_master.SetColumn(0, "CS141_CST", ds_list2.GetColumn(row, "CS122_CST"));	// 거래처
	ds_master.SetColumn(0, "CS141_BSU", ds_list2.GetColumn(row, "CS141_BSU"));	// 업체 코드

	// 등록일, 등록자 insert or update (20080602)	
	if(gbn=="A5"){
		ds_master.SetColumn(0, "CS141_FJD", gfn_getCurrDate());
		ds_master.SetColumn(0, "CS141_FJJ", g_user_id);

	} else if(gbn=="A6") {
		ds_master.SetColumn(0, "CS141_FJD", ds_List2.GetColumn(row, "CS142_FJD"));
		ds_master.SetColumn(0, "CS141_FJJ", ds_List2.GetColumn(row, "CS142_FJJ"));

		ds_master.SetColumn(0, "CS141_FSD", gfn_getCurrDate());
		ds_master.SetColumn(0, "CS141_FSJ", g_user_id);

	} else if(gbn=="A2" && uGroup=="20") {
		ds_master.SetColumn(0, "CS141_FJD", gfn_getCurrDate());
		ds_master.SetColumn(0, "CS141_FJJ", g_user_id);

	} else if(gbn=="A2" && uGroup=="30") {
		ds_master.SetColumn(0, "CS141_FJD", ds_List2.GetColumn(row, "CS142_FJD"));
		ds_master.SetColumn(0, "CS141_FJJ", ds_List2.GetColumn(row, "CS142_FJJ"));

		ds_master.SetColumn(0, "CS141_FSD", gfn_getCurrDate());
		ds_master.SetColumn(0, "CS141_FSJ", g_user_id);

	} else if(gbn=="A2" && uGroup=="40") {
		ds_master.SetColumn(0, "CS141_FJD", ds_List2.GetColumn(row, "CS142_FJD"));
		ds_master.SetColumn(0, "CS141_FJJ", ds_List2.GetColumn(row, "CS142_FJJ"));

		ds_master.SetColumn(0, "CS141_FSD", gfn_getCurrDate());
		ds_master.SetColumn(0, "CS141_FSJ", g_user_id);

	} else {
		ds_master.SetColumn(0, "CS141_FJD", ds_List2.GetColumn(row, "CS142_FJD"));
		ds_master.SetColumn(0, "CS141_FJJ", ds_List2.GetColumn(row, "CS142_FJJ"));
		ds_master.SetColumn(0, "CS141_FSD", ds_List2.GetColumn(row, "CS142_FSD"));
		ds_master.SetColumn(0, "CS141_FSJ", ds_List2.GetColumn(row, "CS142_FSJ"));
	}

	// SEQ setting
	var existSeq = false;
	if(ds_List2.GetColumn(row, "CHECK")==1 && length(ds_List2.GetColumn(row, "CS141_SEQ"))>0){
		ds_master.SetColumn(0, "CS141_SEQ", ds_List2.GetColumn(row, "CS141_SEQ"));	// SEQ
		existSeq = true;
	} else {		
		for(var i=0; i<ds_List2.GetRowCount(); i++){
			var preSeq = ds_List2.GetColumn(i, "CS141_SEQ");

			if(length(preSeq)>0 && length(ds_master.GetColumn(0, "CS141_SEQ"))<=0){					
				if( parseInt(right(ds_List2.GetColumn(i, "CS142_PST"),1))<3 ){
					ds_master.SetColumn(0, "CS141_SEQ", preSeq);	// SEQ
					existSeq = true;
				} else {
					ds_master.SetColumn(0, "CS141_SEQ", "");
				}				
			}	
		}
	}

	// ds_detail setting
	for(var i=0; i<ds_List2.GetRowCount(); i++){
		if(ds_List2.GetColumn(i, "CHECK")==1){

			dtlRow = ds_detail.AddRow();			
			if(existSeq && length(ds_List2.GetColumn(i, "CS142_PST"))>0){
				ds_detail.SetColumn(dtlRow, "ISEXIST", "Y");
			} else {
				ds_detail.SetColumn(dtlRow, "ISEXIST", "N");
			}
			ds_detail.SetColumn(dtlRow, "MAXSEQ", ds_per_val.GetColumn(0, "MAXSEQ"));
			var usd_date = ds_List2.GetOrgColumn(i, "START_DT");
			var can_date = ds_List2.GetOrgColumn(i, "CS142_JHD");
			if(usd_date == can_date) {
				ds_detail.SetColumn(dtlRow, "UMS", 0);
			} else {
				ds_detail.SetColumn(dtlRow, "UMS", gfn_getMonthCnt(ds_List2.GetColumn(i, "START_DT"),ds_List2.GetColumn(i, "CS142_JHD")));
			}
			ds_detail.SetColumn(dtlRow, "CS142_PST", gbn);
			ds_detail.SetColumn(dtlRow, "CS142_GBN", cb_statusG.Value);
			ds_detail.SetColumn(dtlRow, "MANDT",     ds_master.GetColumn(0, "MANDT"));
			ds_detail.SetColumn(dtlRow, "CS142_UPN", ds_master.GetColumn(0, "CS141_UPN"));
			ds_detail.SetColumn(dtlRow, "CS142_CST", ds_master.GetColumn(0, "CS141_CST"));
			ds_detail.SetColumn(dtlRow, "CS142_SEQ", ds_master.GetColumn(0, "CS141_SEQ"));
			ds_detail.SetColumn(dtlRow, "CS142_PJT", ds_List2.GetColumn(i, "CS122_PJT"));
			ds_detail.SetColumn(dtlRow, "CS142_HNO", ds_List2.GetColumn(i, "CS122_HNO"));
			ds_detail.SetColumn(dtlRow, "CS142_BSU", ds_List2.GetColumn(i, "CS141_BSU"));	// 업체 코드
			ds_detail.SetColumn(dtlRow, "CS142_GSQ", ds_List2.GetColumn(i, "CURR_SEQ"));
			ds_detail.SetColumn(dtlRow, "CS142_GND", ds_List2.GetColumn(i, "GBN_CODE"));

			ds_detail.SetColumn(dtlRow, "CS142_FBD", gfn_getCurrDate());
			ds_detail.SetColumn(dtlRow, "CS142_FBJ", ds_master.GetColumn(0, "CS141_FBJ"));
			ds_detail.SetColumn(dtlRow, "CS142_USD", ds_List2.GetColumn(i, "START_DT"));
			ds_detail.SetColumn(dtlRow, "CS142_UMR", ds_List2.GetColumn(i, "END_DT"));
			ds_detail.SetColumn(dtlRow, "CS142_MNY", decode(ds_List2.GetColumn(i, "MON_INFO_ACT"),'',0,null,0,ds_List2.GetColumn(i, "MON_INFO_ACT")));

			//  (반송 || 작성) -> 발송 일 경우 (20080602)			
			if((gbn=="A2" || gbn=="A3") && ds_List2.GetColumn(i, "CS142_PST")=="A1"){
				ds_detail.SetColumn(dtlRow, "CS142_FSA", '');
				ds_detail.SetColumn(dtlRow, "CS142_REQ", '');
				ds_detail.SetColumn(dtlRow, "CS142_SCP", '');
				ds_detail.SetColumn(dtlRow, "CS142_UNT", '');
				ds_detail.SetColumn(dtlRow, "CS142_AMT", '');
				if(gbn=="A3") {
					ds_detail.SetColumn(dtlRow, "CS142_JHD", cal_fail_dt.Value);
				} else {
					ds_detail.SetColumn(dtlRow, "CS142_JHD", '');
				}
				ds_detail.SetColumn(dtlRow, "CS142_RLT", '');
				ds_detail.SetColumn(dtlRow, "CS142_RMK", '');
				ds_detail.SetColumn(dtlRow, "CS142_YMD", '');
			} else {
				ds_detail.SetColumn(dtlRow, "CS142_FSA", TextArea00.Text);
				ds_detail.SetColumn(dtlRow, "CS142_REQ", TextArea1.Text);
				ds_detail.SetColumn(dtlRow, "CS142_SCP", Edit3.Text);			
				ds_detail.SetColumn(dtlRow, "CS142_UNT", decode(MaskEdit0.value,'',0,null,0,MaskEdit0.value));
				ds_detail.SetColumn(dtlRow, "CS142_AMT", decode(MaskEdit1.value,'',0,null,0,MaskEdit1.value));
				ds_detail.SetColumn(dtlRow, "CS142_JHD", cal_fail_dt.Value);
				ds_detail.SetColumn(dtlRow, "CS142_RLT", TextArea0.Text);			
				ds_detail.SetColumn(dtlRow, "CS142_RMK", TextArea2.Text);
				ds_detail.SetColumn(dtlRow, "CS142_YMD", Calendar0.value);	
			}

			// 등록일, 등록자 insert or update (20080602)	
			if(gbn=="A5"){
				ds_detail.SetColumn(dtlRow, "CS142_FJD", gfn_getCurrDate());
				ds_detail.SetColumn(dtlRow, "CS142_FJJ", g_user_id);
			} else if(gbn=="A6") {
				ds_detail.SetColumn(dtlRow, "CS142_FJD", ds_List2.GetColumn(i, "CS142_FJD"));
				ds_detail.SetColumn(dtlRow, "CS142_FJJ", ds_List2.GetColumn(i, "CS142_FJJ"));

				ds_detail.SetColumn(dtlRow, "CS142_FSD", gfn_getCurrDate());
				ds_detail.SetColumn(dtlRow, "CS142_FSJ", g_user_id);
			} else if(gbn=="A2" && uGroup=="20") {
				ds_detail.SetColumn(dtlRow, "CS142_FJD", gfn_getCurrDate());
				ds_detail.SetColumn(dtlRow, "CS142_FJJ", g_user_id);
			} else if(gbn=="A2" && uGroup=="30") {
				ds_detail.SetColumn(dtlRow, "CS142_FJD", ds_List2.GetColumn(i, "CS142_FJD"));
				ds_detail.SetColumn(dtlRow, "CS142_FJJ", ds_List2.GetColumn(i, "CS142_FJJ"));

				ds_detail.SetColumn(dtlRow, "CS142_FSD", gfn_getCurrDate());
				ds_detail.SetColumn(dtlRow, "CS142_FSJ", g_user_id);
			} else if(gbn=="A2" && uGroup=="40") {
				ds_detail.SetColumn(dtlRow, "CS142_FJD", ds_List2.GetColumn(i, "CS142_FJD"));
				ds_detail.SetColumn(dtlRow, "CS142_FJJ", ds_List2.GetColumn(i, "CS142_FJJ"));

				ds_detail.SetColumn(dtlRow, "CS142_FSD", gfn_getCurrDate());
				ds_detail.SetColumn(dtlRow, "CS142_FSJ", g_user_id);
			} else {
				ds_detail.SetColumn(dtlRow, "CS142_FJD", ds_List2.GetColumn(i, "CS142_FJD"));
				ds_detail.SetColumn(dtlRow, "CS142_FJJ", ds_List2.GetColumn(i, "CS142_FJJ"));
				ds_detail.SetColumn(dtlRow, "CS142_FSD", ds_List2.GetColumn(i, "CS142_FSD"));
				ds_detail.SetColumn(dtlRow, "CS142_FSJ", ds_List2.GetColumn(i, "CS142_FSJ"));
			}
		}
	}

	// 저장 처리 
	tit_callService(
        "CS1301001B_ACT"
        , "save"
        , "ds_master=ds_master ds_detail=ds_detail"
        , ""
        , ""
        , "fn_afterSave");

}
