function ds_list2_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID=="CHECK" && varNewValue=="1"){
		if(ds_List2.GetColumn(nRow, "GBN_CODE")!="D"){
			gfn_showMsg('CW00093');
			ds_list2.SetColumn(nRow, "CHECK", varOldValue);
			return;
		}
		if(length(ds_List2.GetColumn(nRow, "FSD"))>0){
			gfn_showMsg('CW00094');
			return;
		}
	}

}
