function btn_fail_print_OnClick(obj)
{
	var insRow;
	var t_amt = 0;

	for(var i=0; i<ds_list2.RowCount(); i++) {
		if(i == 0) {
			insRow = ds_list4.AddRow();			
			ds_list4.SetColumn(insRow, "JHD", ds_list2.GetColumn(0, "CS142_JHD"));
			ds_list4.SetColumn(insRow, "MNY", ds_list2.GetColumn(0, "MON_INFO"));
			ds_list4.SetColumn(insRow, "FSA", ds_list2.GetColumn(0, "CS142_FSA"));
			ds_list4.SetColumn(insRow, "REQ", ds_list2.GetColumn(0, "CS142_REQ"));
			ds_list4.SetColumn(insRow, "RLT", ds_list2.GetColumn(0, "CS142_RLT"));
			ds_list4.SetColumn(insRow, "RMK", ds_list2.GetColumn(0, "CS142_RMK"));
			ds_list4.SetColumn(insRow, "SCP", ds_list2.GetColumn(0, "CS142_SCP"));
			ds_list4.SetColumn(insRow, "UNT", ds_list2.GetColumn(0, "CS142_UNT"));
			ds_list4.SetColumn(insRow, "UPN", ds_list2.GetColumn(0, "CS142_UPN"));
			ds_list4.SetColumn(insRow, "YMD", ds_list2.GetColumn(0, "CS142_YMD"));
			ds_list4.SetColumn(insRow, "AMT", ds_list2.GetColumn(0, "CS142_AMT"));
			ds_list4.SetColumn(insRow, "BSU_NM", ds_list2.GetColumn(0, "BSU_NM"));
			ds_list4.SetColumn(insRow, "ARA_NM", ds_list2.GetColumn(0, "ARA_NM"));
			ds_list4.SetColumn(insRow, "SPT", ds_list2.GetColumn(0, "SPT"));
			ds_list4.SetColumn(insRow, "CNT", ds_list2.GetRowCount());
			ds_list4.SetColumn(insRow, "CDATE", ds_list2.GetColumn(0, "CDATE"));
			ds_list4.SetColumn(insRow, "EPN", ds_list2.GetColumn(0, "EPN"));
			ds_list4.SetColumn(insRow, "NAM", ds_list2.GetColumn(0, "NAM"));
			ds_list4.SetColumn(insRow, "ZSPEC1", ds_list2.GetColumn(0, "ZSPEC1"));
			ds_list4.SetColumn(insRow, "MAMT", 0);
		}

		t_amt = t_amt + ds_list2.GetColumn(i, "MON_INFO");

		if(i == (ds_list2.RowCount() - 1)) {
			ds_list4.SetColumn(0, "MAMT", t_amt);
		}
	}
	//alert(ds_list4.SaveXML());
	//tit_showDs(ds_list4.SaveXML());
	//UbiReport Call
	Dialog("CS13::CS1301001C.xml", "");
}
