function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);
	if(ds_cond.GetRowCount() < 1) ds_cond.AddRow();

	var codeArr = ["CS117","CS156","CS157"];
    var dsObjArr = [ds_status,ds_sel,ds_fail];
    var addTypeArr = [G_CODE_ALL,G_CODE_ALL,G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);
}

function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_fail.Index = 0;
	cb_sel.Index = 0;
	cb_gbn.Index = 0;
	cb_status.Index = 0;
	cal_startDt.Value = AddMonth(gfn_getCurrDate(), -1);
	cal_endDt.Value = AddMonth(gfn_getCurrDate(), 1);
	ed_pjt.Enable = false;
	ed_upn.Enable = false;
	ed_cst.Enable = false;
	btn_posid.Enable = false;
	btn_popProj.Enable = false;
	cb_fail.Enable = false;
}
