/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	gfn_clearMsgBar();

	var user_grp = gfn_getUserGroup();

	if(gfn_isNull(cal_startDt, true)) {
		var arr = ["조회일자"];
		gfn_showMsg("CW00001", arr);
		return;
	}

	if(gfn_isNull(cal_endDt, true)) {
		var arr = ["조회일자"];
		gfn_showMsg("CW00001", arr);
		return;
	}

	if(ed_upn.value > "") {
		if(gfn_isNull(ed_cst, true)) {
			var arr = [st_upn.value];
			gfn_showMsg("CW00001", arr);
			return;
		}
	}
/*
	if(gfn_isNull(cb_gbn, true)) {
		var arr = [st_gbn.value];
		gfn_showMsg("CW00001", arr);
		return;
	}
*/
	if(user_grp == "40") {
		if(gfn_isNull(div_code.cb_big, true)) {
			var arr = [st_bsu.value];
			gfn_showMsg("CW00001", arr);
			return;
		}
	}

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		}
	}

	ds_cond.SetColumn(0, "SDT", substr(cal_startDt.Value,0,6) + "01");
	ds_cond.SetColumn(0, "EDT", gfn_getLeapLastDay(substr(cal_endDt.Value,0,6)+"01"));	
	ds_cond.SetColumn(0, "DGBN", st_ym.Value);
	ds_cond.SetColumn(0, "UPN", ed_upn.Text);
	ds_cond.SetColumn(0, "CST", ed_cst.Text);
	ds_cond.SetColumn(0, "PJT", ed_pjt.Text);
	ds_cond.SetColumn(0, "SEL", cb_sel.Value);
	ds_cond.SetColumn(0, "FAIL", cb_fail.Value);
	ds_cond.SetColumn(0, "PST", cb_status.Value);
	ds_cond.SetColumn(0, "JPGBN", cb_jpgbn.Value);

	// sort mark 삭제
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;
	ds_list.ClearData();

	tit_clearActionInfo();
	if ( substr(cal_startDt.Value,0,6) == substr(cal_endDt.Value,0,6)) {
		tit_addSearchActionInfo("cs13:CS1304001A_S3");
	} else {
		tit_addSearchActionInfo("cs13:CS1304001A_S1");
	}

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 삽입 20
********************************************************************************/
function fn_input() {
	gfn_clearMsgBar();

	if(gfn_isNull(cb_gbn, true)) {
		var arr = [st_gbn.value];
		gfn_showAlert("CW00001", arr);
		return;
	}

	if(gfn_isNull(cb_jpgbn, true)) {
		var arr = [st_jpgbn.value];
		gfn_showAlert("CW00001", arr);
		return;
	}

	if(cb_gbn.Value == "A" || cb_gbn.Value == "B") {
		if(gfn_isNull(ed_pjt, true)) {
			var arr = [st_pjt.value];
			gfn_showAlert("CW00001", arr);
			return;
		}
	} else {
		if(gfn_isNull(ed_upn, true)) {
			var arr = [st_upn.value];
			gfn_showAlert("CW00001", arr);
			return;
		}

		if(gfn_isNull(ed_cst, true)) {
			var arr = [st_upn.value];
			gfn_showAlert("CW00001", arr);
			return;
		}
	}

	fn_chkFailReport(); // 기 작성된 실패 보고 중 진행 중인 내용 존재 여부 체크

    if(ds_chkFail.RowCount() > 0) {
		var text1 = ds_chkFail.GetColumn(0, "CS144_GNO") ;
		var text2 = ds_chkFail.GetColumn(0, "CS143_SEQ") ;
		var text3 = "실패보고 진행 중 입니다." + "\r\n" 
		             + "아래 현장을 확인 하세요." + "\r\n"
		             + "- 프로젝트 : " + text1  + "\r\n"
		             + "- 관리번호 : " + text2 ;

		gfn_openMsgWin( text3 ,"W"); // 타이틀 warning massage
		return;

    } else {

		var args = "seq=''" + " nam=''" + " spt=''"
				+ " pjt=" + quote(ed_pjt.Text)
				+ " isd=''"
				+ " upn=" + quote(ed_upn.Text)
				+ " cst=" + quote(ed_cst.Text)
				+ " gnd=''"
				+ " gbn=" + quote(cb_gbn.Value) + " frm='A'"
				+ " jpgbn=" + quote(cb_jpgbn.Value) ;

		gfn_openPopup("CS1304001B", true, args);
    }

}

/********************************************************************************
* 기      능   : 기 작성된 실패 보고 중 진행 중인 내용 존재 여부 체크
********************************************************************************/
function fn_chkFailReport() {

	ds_chkFail.ClearData();

	tit_clearActionInfo();																	
	tit_addSearchActionInfo("cs13:CS1304001A_S2");									
	tit_callService(		
			""
			, ""							
			, "ds_cond=ds_cond"							
			, "ds_chkFail=ds_chkFail"							
			, ""							
			, ""							
			, true							
			, false							
			, true); 


}
