}

function cb_gbn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if(cb_gbn.Value == "A") {
		ed_pjt.Enable = true;
		ed_upn.Enable = false;
		ed_cst.Enable = false;
		btn_posid.Enable = true;
		btn_popProj.Enable = false;
		ed_upn.Text = "";
		ed_cst.Text = "";
	} else if(cb_gbn.Value == "B") {
		ed_pjt.Enable = true;
		ed_upn.Enable = false;
		ed_cst.Enable = false;
		btn_posid.Enable = true;
		btn_popProj.Enable = false;
		ed_upn.Text = "";
		ed_cst.Text = "";
	} else if(cb_gbn.Value == "C") {
		ed_pjt.Enable = false;
		ed_upn.Enable = true;
		ed_cst.Enable = true;
		btn_posid.Enable = false;
		btn_popProj.Enable = true;
		ed_pjt.Text = "";
	} else {
		ed_pjt.Enable = false;
		ed_upn.Enable = false;
		ed_cst.Enable = false;
		btn_posid.Enable = false;
		btn_popProj.Enable = false;
		ed_pjt.Text = "";
		ed_upn.Text = "";
		ed_cst.Text = "";
	}
}

function cb_sel_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if(cb_sel.Value == "A") {
		cb_fail.Index = 0;
		cb_fail.Enable = true;
		//실패그룹핑 변경 2015.01.20 YAR
		ds_fail.Filter("CODE <= 'M99' AND CODE <> 'M06' AND CODE <> 'M08' AND CODE <> 'M12' AND CODE <> 'M99'");
	} else if(cb_sel.Value == "B") {
		cb_fail.Index = 0;
		cb_fail.Enable = true;
		ds_fail.Filter("(CODE = '' OR (CODE > 'M99' AND CODE < 'U99')) AND CODE <> 'U05' AND CODE <> 'U15'");
	} else {
		cb_fail.Index = 0;
		cb_fail.Enable = false;
	}
}
function btn_wf_stat_OnClick(obj)
{
	var docNo;
	docNo = ds_list.GetColumn(grd_list.CurrentRow, "DOCNO");	

	ds_cond.SetColumn(0, "DOCNO", docNo);

	tit_clearActionInfo();														
	tit_addSingleActionInfo("cs01:CS0101008A_S3");								//결재상세정보를 가져온다.
	tit_callService("", "", "ds_cond=ds_cond", "ds_wf_stat=ds_wf_stat", "", "", true, false,true);

	if(ds_wf_stat.rowcount <= 0)
	{
		return;
	}
	var statStr = "";

	for(var iCnt = ds_wf_stat.rowcount - 1; iCnt >= 0; iCnt--)
	{
		statStr += ds_wf_stat.GetColumn(iCnt, "APPGUBNNM") + " / " + 
				   ds_wf_stat.GetColumn(iCnt, "SEQNONM")  + " / " + 
				   ds_wf_stat.GetColumn(iCnt, "SEQSTATNM")  + "\n    " + 
				   ds_wf_stat.GetColumn(iCnt, "APDPT1_N")  + " / " + 
				   ds_wf_stat.GetColumn(iCnt, "APRNK")  + " / " + 
				   ds_wf_stat.GetColumn(iCnt, "APIRUM")  + "\n";				   	
	}

	gfn_openMsgWin(statStr, "", "A", docNo + " 결재상태");
}

