// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);
	cb_gubun.Index=0;
	ufn_chgGubun();
	cb_year.Value= substr(gfn_getCurrDate(),0,4);
	cb_month.Value=substr(gfn_getCurrDate(),4,2);

	//공통코드
	var codeArr = ["CS115","CS116"];	
	var dsObjArr = [ds_bsu1,ds_cyGubun];
	var addTypeArr = [G_CODE_NONE,G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);	
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	var user_grp = gfn_getUserGroup();

	if(user_grp == "30") { // 지방사무소일 경우
		ds_bsu1.filter("CODE == " + quote(G_AREA_CODE));
		cb_bsu1.Enable = false;
	} else if(user_grp == "40") { // 고객지원부일 경우
		cb_bsu1.Enable = true;
	}

	cb_juj.Index = 0;
	cb_buj.Index = 0;
	cb_bsu1.Index = 0;
	cb_cyGubun.Index = 0;

	ufn_setBsu();
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
