/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	if(fn_queryCheck()){
		tit_clearActionInfo();
		if(ds_cond.GetColumn(0,"GUBUN")=="A" && (cb_year.Value=="" && cb_month.Value=="")){
			tit_addSearchActionInfo("cs14:CS1401001A_S2");
		}
		else if(ds_cond.GetColumn(0,"GUBUN")=="B" && (cb_year.Value=="" && cb_month.Value=="")){
			tit_addSearchActionInfo("cs14:CS1401001A_S3");
		}		
		else if(cb_year.Value!="" && cb_month.Value!=""){
			ds_cond.SetColumn(0,"YYYYMM",cb_year.Value + cb_month.Value);
			tit_addSearchActionInfo("cs14:CS1401001A_S4");
		}			

		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list=ds_list"
			, ""
			, "fn_afterQuery"
			, true);        
	}
}
function fn_queryCheck(){
	if(ds_cond.GetColumn(0,"GUBUN")=="A"){//무상
		if(ed_pjt.Text==""){
			//alert("Pjt No.를 입력하세요.");
			gfn_showAlert("CW00001","Pjt No.");
			return false;
		}
	}
	else if(ds_cond.GetColumn(0,"GUBUN")=="B"){//유상
		if(ed_upn.Text==""){
			//alert("통합 Pjt No.를 입력하세요.");
			gfn_showAlert("CW00001","통합 Pjt No.");
			return false;
		}
		if(ed_cst.Text==""){
			//alert("거래처를 입력하세요.");
			gfn_showAlert("CW00001","거래처");
			return false;
		}
	}

	if((cb_year.Value!="" && cb_month.Value=="") || (cb_year.Value=="" && cb_month.Value!="")){
		//alert("이관대상년월을 입력하세요.");
		gfn_showAlert("CW00001","이관대상년월");
		return false;	
	}
	return true;
}
/********************************************************************************
* 기      능   : 삽입 20
********************************************************************************/
function fn_insert() {
	// 그리드에 row 추가 
	var newRow = gfn_addGridRow(grd_list, -1, "FLAG");
}
/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() {
	if(fn_deleteCheck()==true){
		tit_clearActionInfo();

		tit_addSaveActionInfo(
			""
			, ""
			, "cs14:CS1401001A_D1"
			, "FLAG"
			);		
		tit_callService(
			""
			, ""
			, "ds_list=ds_list"
			, ""
			, "UPN="+quote(ed_upn.Text)+" CST="+quote(ed_cst.Text)+" IGM="+quote(cb_year.Text+cb_month.Text)+" GBATEXT="+quote(cb_cyGubun.Value)
			, "fn_query"
			, true
			, true
			, true
			);      	
	}
}
function fn_deleteCheck(){
	for(var i=0;i<ds_list.GetRowCount();i++){
		if(ds_list.GetColumn(i,"CHECK")=="1"){			
			ds_list.SetColumn(i,"FLAG","D");		

			ds_cntCond.SetColumn(0,"UPN",ed_upn.Text);
			ds_cntCond.SetColumn(0,"CST",ed_cst.Text);
			ds_cntCond.SetColumn(0,"PJT",ds_list.GetColumn(i,"PJT"));
			ds_cntCond.SetColumn(0,"HNO",ds_list.GetColumn(i,"HNO"));

			tit_clearActionInfo();
			tit_addSearchActionInfo("cs14:CS1401001A_S9");
			tit_callService(
				""
				, ""
				, "ds_cntCond=ds_cntCond"
				, "ds_cnt=ds_cnt"
				, ""
				, ""
				, true
				, true
				, true
				); 

			if(ds_cnt.GetColumn(0,"COUNT")>0){
				//alert("삭제가 불가합니다.");
				gfn_showAlert("CW00072");
				ds_list.SetColumn(i,"CHECK","0");	
				return false;					
			}			
		}
		else if(ds_list.GetColumn(i,"CHECK")=="0"){			
			ds_list.SetColumn(i,"FLAG","");			
		}		
	}
	return true;
}

/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	if(fn_saveCheck()==true){
		tit_clearActionInfo();

		tit_addSaveActionInfo(
			""
			, "cs14:CS1401001A_I1"
			, ""
			, "FLAG"
			);		
		tit_callService(
			""
			, ""
			, "ds_list=ds_list"
			, ""
			, "UPN="+quote(ed_upn.Text)+" CST="+quote(ed_cst.Text)+" IGM="+quote(cb_year.Text+cb_month.Text)+" GBATEXT="+quote(cb_cyGubun.Value)
			, ""
			, true
			, true
			, true
			);      	
	}
}
function fn_saveCheck(){
	ds_data.SetColumn(0,"UPN",ds_cond.GetColumn(0,"UPN"));
	ds_data.SetColumn(0,"CST",ds_cond.GetColumn(0,"CST"));
	ds_data.SetColumn(0,"PJT",ds_cond.GetColumn(0,"PJT"));

	if(ds_data.GetColumn(0,"YYYY")==""){
		//alert("이관대상년을 선택하십시오.");
		gfn_showAlert("CW00001","이관대상년");
		return false;
	}
	if(ds_data.GetColumn(0,"MM")==""){
		//alert("이관대상월을 선택하십시오.");
		gfn_showAlert("CW00001","이관대상월");
		return false;
	}
	if(ds_data.GetColumn(0,"BSU2")==""){
		//alert("이관후 보수협력사를 선택하십시오.");
		gfn_showAlert("CW00001","이관후 보수협력사");
		return false;
	}
	if(ds_data.GetColumn(0,"CYGUBUN")==""){
		//alert("시내외구분을 선택하십시오.");
		gfn_showAlert("CW00001","시내외구분");
		return false;
	}
	if(length(trim(cb_juj.Value)) == 0 && length(trim(cb_buj.Value)) == 0) {
		//alert("주점검자나 부점검자를 선택하십시오.");
		gfn_showAlert("CW00116","주점검자나 부점검자를");
		return false;
	}

	for(var i=0;i<ds_list.GetRowCount();i++){
		if(ds_list.GetColumn(i,"CHECK")=="1"){			
			ds_list.SetColumn(i,"FLAG","U");			
			if(cb_gubun.Text!=substr(ds_list.GetColumn(i,"BALJUTEXT"),0,2)){
				//alert("선택한 자료구분과 실제계약정보가 불일치합니다.");		
				gfn_showAlert("CW00098");		
				ds_list.SetColumn(i,"CHECK","0");	
				ds_list.SetColumn(i,"FLAG","");			
				return false;
			}

			if(cb_juj.Value == cb_buj.Value) {
				//alert("주점검자와 부점검자가 동일인이 될 수 없습니다.");
				gfn_showAlert("주점검자와 부점검자가 동일인이 될 수 없습니다.");
				ds_list.SetColumn(i,"CHECK","");
				return false;
			}

			ds_lgortCond.SetColumn(0,"LGORTBEFORE",ds_list.GetColumn(i,"LGORTBEFORE"));
			ds_lgortCond.SetColumn(0,"LGORTAFTER",ds_data.GetColumn(0,"BSU2"));

			if(fn_saveSetIht()==false){
				ds_list.SetColumn(i,"CHECK","0");	
				ds_list.SetColumn(i,"FLAG","");
				return false;
			}

			ds_list.SetColumn(i,"LGORTAFTER",ds_lgort.GetColumn(0,"LGORTAFTER"));
			ds_list.SetColumn(i,"GHB",ds_lgort.GetColumn(0,"GHB"));
			ds_list.SetColumn(i,"GHA",ds_lgort.GetColumn(0,"GHA"));
			ds_list.SetColumn(i,"IHT",ds_lgort.GetColumn(0,"IHT"));	
			ds_list.SetColumn(i,"JUA",cb_juj.Value);
			ds_list.SetColumn(i,"BUA",cb_buj.Value);

			ds_cntCond.SetColumn(0,"UPN",ed_upn.Text);
			ds_cntCond.SetColumn(0,"CST",ed_cst.Text);
			ds_cntCond.SetColumn(0,"PJT",ds_list.GetColumn(i,"PJT"));
			ds_cntCond.SetColumn(0,"HNO",ds_list.GetColumn(i,"HNO"));
			ds_cntCond.SetColumn(0,"YYYYMM",cb_year.Text+cb_month.Text);			

			if(fn_saveCntCheck()==false){	
				ds_list.SetColumn(i,"CHECK","0");	
				ds_list.SetColumn(i,"FLAG","");
				return false;
			}				
			var baljuCode = ds_list.GetColumn(i,"BALJUCODE");
			if(baljuCode=="C" || baljuCode=="D"){
				if(fn_saveDateCheck1()==false){	
					ds_list.SetColumn(i,"CHECK","0");	
					ds_list.SetColumn(i,"FLAG","");
					return false;
				}				
			}
			else if(baljuCode=="A" || baljuCode=="B"){
				if(fn_saveDateCheck2()==false){	
					ds_list.SetColumn(i,"CHECK","0");	
					ds_list.SetColumn(i,"FLAG","");
					return false;
				}	
			}			
		}	
		else if(ds_list.GetColumn(i,"CHECK")=="0"){			
			ds_list.SetColumn(i,"FLAG","");			
		}
	}
	return true;
}
function fn_saveSetIht(){
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs14:CS1401001A_S8");
	tit_callService(
		""
		, ""
		, "ds_lgortCond=ds_lgortCond"
		, "ds_lgort=ds_lgort"
		, ""
		, ""
		, true
		, true
		, true
		); 

	if(ds_lgort.GetColumn(0,"GHB")=="1" && ds_lgort.GetColumn(0,"GHA")=="1"){		
		//alert("직영간의 이관은 불가합니다.");
		gfn_showAlert("CW00099");	
		return false;					
	}	
	return true;
}
function fn_saveCntCheck(){
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs14:CS1401001A_S5");

	tit_callService(
		""
		, ""
		, "ds_cntCond=ds_cntCond"
		, "ds_cnt=ds_cnt"
		, ""
		, ""
		, true
		, true
		, true
		);    
	if(ds_cnt.GetColumn(0,"COUNT")>0){
		//alert("해당월에는 이미 이관요청 작업을 실시하였습니다.(익월로 처리가능)");
		gfn_showAlert("CW00100");
		return false;
	}
	return true;
}

