/********************************************************************************
* 기      능   : 사용자 정의 함수  
********************************************************************************/
function ufn_chgGubun() {
	if(cb_gubun.Value=="A"){
		ed_upn.Enable = false;
		ed_cst.Enable = false;
		ed_upn.Text = "";
		ed_cst.Text = "";
		btn_upn.Enable = false;
		ed_pjt.Enable = true;
		btn_pjt.Enable = true;
	}
	else if(cb_gubun.Value=="B"){
		ed_upn.Enable = true;
		ed_cst.Enable = true;
		btn_upn.Enable = true;
		ed_pjt.Enable = false;
		btn_pjt.Enable = false;	
		ed_pjt.Text = "";
	}
}
function ufn_openUpn() {
	var arg = "upn="+quote(ed_upn.Text);
	gfn_openPopup("CS9601001A",true,arg);
}
function ufn_openPjt() {
	var arg = "pjt="+quote(ed_pjt.Text);
	gfn_openPopup("CS9601002A",true,arg);
}
function ufn_setBsu() {	
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs14:CS1401001A_S1");

	tit_callService(
		""
		, ""
		, "ds_data=ds_data"
		, "ds_bsu2=ds_bsu2"
		, ""
		, ""
		, true
		, true
		, true
		);

}

