/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}

function cb_bsu2_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs19:CS1901002A_S1");
	tit_addSearchActionInfo("cs19:CS1901002A_S2");		

	tit_callService(
        ""
        , ""
        , ""
        , "ds_juj=ds_juj ds_buj=ds_buj"
        , "BSU="+quote(cb_bsu2.Value)
        , "fn_afterJuj"
        , true
        , true
        , true
        );
}

function fn_afterJuj(errCode, errMsg) {
	ds_juj.Filter("CODE > ''");
	ds_juj.InsertRow(0);
	ds_juj.SetColumn(0, "CODE", "");
	ds_juj.SetColumn(0, "CODE_NAME", "- 선택 -");
	cb_juj.Index = 0;

	ds_buj.Filter("CODE > ''");
	ds_buj.InsertRow(0);
	ds_buj.SetColumn(0, "CODE", "");
	ds_buj.SetColumn(0, "CODE_NAME", "- 선택 -");
	cb_buj.Index = 0;
}
