function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);
	cb_gubun.Index=0;
	ufn_chgGubun();
	med_yymm.Value = substr(gfn_getCurrDate(),0,6);

	//공통코드
	var codeArr = ["CS115","CSRSN"];	
	var dsObjArr = [ds_bsu1,ds_rsn2];
	var addTypeArr = [G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);	
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	var user_grp = gfn_getUserGroup();



	if(user_grp == "30") { // 지방사무소일 경우
	//	ds_bsu1.filter("CODE == " + quote(G_AREA_CODE));	//2015.02.26 YAR 타지역 이관작업 가능
	//	cb_bsu1.Enable = false;
		ufn_enableBtn("input", false);
	} else if(user_grp == "20" || user_grp == "40") { // 고객지원부일 경우
		cb_bsu1.Enable = true;

		// 유저별 입력버튼 권한 부여 -> 공통함수로 대체
		// if(G_USER_ID == "2017938"               // 최인식
		// || G_USER_ID == "2021153" || G_USER_ID == "T021153" || G_USER_ID == "2016869" //임유진, 신은정-고객지원부 
		// || G_USER_ID == "2010984" || G_USER_ID == "2021056" || G_USER_ID == "3014312"  // 김재순 전현정 김정운
		// || G_USER_ID == "2020882" || G_USER_ID == "2021200" || G_USER_ID == "2018900" || G_USER_ID == "2029758" || G_USER_ID == "2999999" // 이동진 이하림  방기태 한정훈 김종
		// || G_USER_ID == "2019175" || G_USER_ID == "3016544") { // 박준성 이재성
			// ufn_enableBtn("input", true);
		// } else {
			// ufn_enableBtn("input", false);
		// }
		gfn_initDs(ds_rcond, true);
		ds_rcond.SetColumn(0, "ZPROGRAM", "CS1403001A");
		ds_rcond.SetColumn(0, "GBN", "01");	
		input_auth = gfn_getAuth(ds_rcond);
		ds_rcond.ClearData();
		if (input_auth == "1"){
			ufn_enableBtn("input", true);
		} else {
			ufn_enableBtn("input", false);
		}
		// 유저별 입력버튼 권한 부여 -> 공통함수로 대체 종료
	}

	//권한 임시삭제 2015.01
	/*
	if(user_grp == "20" || user_grp == "30") { 
	//	div_btn.btn_input.Enable = true;
		ufn_enableBtn("input", true);
	} 
	else {
		ufn_enableBtn("input", false);
	}
	*/
	ufn_enableBtn("input", true);

	cb_juj.Index = 0;
	cb_buj.Index = 0;
	cb_bsu1.Index = 0;
	cb_daesang.Index = 0;

	ufn_setBsu();
}

