/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	ds_list.ClearData();

	if(fn_queryCheck()) {
		//ds_cond.SetColumn(0, "ARA", ds_data.GetColumn(0, "BSU1"));
		ds_cond.SetColumn(0, "BSU", ds_data.GetColumn(0, "BSU2"));

		if(chk_fyn.value == true) {
			ds_cond.SetColumn(0, "FYN", "Y");
		} else {
			ds_cond.SetColumn(0, "FYN", "N");
		}

		tit_clearActionInfo();

		if(ds_cond.GetColumn(0,"GUBUN")=="A" && cb_daesang.value == "A") { // 무상 - 이관대상
			ds_cond.SetColumn(0, "YYYYMM", med_yymm.Value);
			tit_addSearchActionInfo("cs14:CS1403001A_S2");
		} else if(ds_cond.GetColumn(0,"GUBUN")=="Z" && cb_daesang.value == "A") { // 미입주 - 이관대상
			ds_cond.SetColumn(0, "YYYYMM", med_yymm.Value);
			tit_addSearchActionInfo("cs14:CS1403001A_S12");
		} else if(ds_cond.GetColumn(0,"GUBUN")=="B" && cb_daesang.value == "A") { // 유상 - 이관대상
			ds_cond.SetColumn(0, "YYYYMM", med_yymm.Value);	
			tit_addSearchActionInfo("cs14:CS1403001A_S3");
		} else if(cb_daesang.value == "B") { // 유상 - 이관완료, 미입주 - 이관완료, 무상 - 이관완료
			ds_cond.SetColumn(0, "YYYYMM", med_yymm.Value);
			tit_addSearchActionInfo("cs14:CS1403001A_S4");
		}			
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list=ds_list"
			, ""
			, "fn_afterQuery"
			, true);        
	}
}

function fn_queryCheck() {
	var user_grp = gfn_getUserGroup();

	//대상이 이관대상인 경우에만 조건 체크
	if(cb_daesang.value == "A" && user_grp <> "40") {
		if(ds_cond.GetColumn(0,"GUBUN") == "A" || ds_cond.GetColumn(0,"GUBUN") == "Z") {//무상,미입주
			if(ed_pjt.Text == "") {
				//alert("Pjt No.를 입력하세요.");
				gfn_showAlert("CW00001","Pjt No.");
				return false;
			}
		} else if(ds_cond.GetColumn(0,"GUBUN") == "B") {//유상
			if(ed_upn.Text == "") {
				//alert("통합 Pjt No.를 입력하세요.");
				gfn_showAlert("CW00001","통합 Pjt No.");
				return false;
			}

			if(ed_cst.Text == "") {
				//alert("거래처를 입력하세요.");
				gfn_showAlert("CW00001","거래처");
				return false;
			}
		}
	}

	if(med_yymm.Value == "") {
		//alert("이관대상년월을 입력하세요.");
		gfn_showAlert("CW00001","이관대상년월");
		return false;	
	}

	return true;
}

