// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	// 작업자가 자회사 직원일때, 조회된 호기의 협력사가 모회사 협력사이면 이관대상에서 제외
	if (G_SAP_USER_VBCD == "H100"){
		if (cb_daesang.value == "A") {
			grd_list.Redraw = false;
			var vLgortBefore = "", vVkbur = ""; 
			for (i = ds_list.GetRowCount() - 1; i > -1; i--){
				if (vLgortBefore != ds_list.GetColumn(i, "LGORTBEFORE")){
					vLgortBefore = ds_list.GetColumn(i, "LGORTBEFORE");
					vVkbur = gfn_getBsu(vLgortBefore, "VKBUR");
				}
				if (vVkbur != G_SAP_USER_VBCD){
					ds_list.DeleteRow(i);
				}
			}
			ds_list.ApplyChange();
			grd_list.Redraw = true;
		}
	}

	// 메시지 처리 
	gfn_showSearchMsg(grd_list);

/***
	var t_upn = ds_cond.GetColumn(0, "UPN");
	var t_pjt = ds_cond.GetColumn(0, "PJT");

	if(length(trim(t_upn)) == 0 && length(trim(t_pjt)) == 0) {
		ufn_enableBtn("save", false);
	} else {
		if(G_USER_ID == "2017750" || G_USER_ID == "2017938" || G_USER_ID == "2018454" || G_USER_ID == "2018438"// 권지예 추가 
