// 사용자 정의 함수는 ufn_xxxx() 형태로 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 사용자 정의 함수  
********************************************************************************/
function ufn_chgGubun() {
	if(cb_gubun.Value == "A") {
		ed_upn.Enable = false;
		ed_cst.Enable = false;
		ed_upn.Text = "";
		ed_cst.Text = "";
		btn_upn.Enable = false;
		ed_pjt.Enable = true;
		btn_pjt.Enable = true;
	} else if(cb_gubun.Value == "B") {
		ed_upn.Enable = true;
		ed_cst.Enable = true;
		btn_upn.Enable = true;
		ed_pjt.Enable = false;
		btn_pjt.Enable = false;	
		ed_pjt.Text = "";
	} else if(cb_gubun.Value == "Z") {
		ed_upn.Enable = false;
		ed_cst.Enable = false;
		ed_upn.Text = "";
		ed_cst.Text = "";
		btn_upn.Enable = false;
		ed_pjt.Enable = true;
		btn_pjt.Enable = true;
	}
}

function ufn_openUpn() {
	var arg = "upn="+quote(ed_upn.Text);
	gfn_openPopup("CS9601001A",true,arg);
}

function ufn_openPjt() {
	var arg = "pjt="+quote(ed_pjt.Text);
	gfn_openPopup("CS9601002A",true,arg);
}

