/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	if(nCell == 0) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} else if(nCell == 1) {
		// 전체 선택
		gfn_selectAllGrid2(obj,1,1,true);	
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}

function cb_bsu2_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs19:CS1901002A_S1");
	tit_addSearchActionInfo("cs19:CS1901002A_S2");		
	tit_callService(
        ""
        , ""
        , ""
        , "ds_juj=ds_juj ds_buj=ds_buj"
        , "BSU="+quote(cb_bsu2.Value)
        , "fn_afterJuj"
        , true
        , true
        , true
        );
}

function fn_afterJuj(errCode, errMsg) {
	ds_juj.Filter("CODE > ''");
	ds_juj.InsertRow(0);
	ds_juj.SetColumn(0, "CODE", "");
	ds_juj.SetColumn(0, "CODE_NAME", "- 선택 -");
	cb_juj.Index = 0;

	ds_buj.Filter("CODE > ''");
	ds_buj.InsertRow(0);
	ds_buj.SetColumn(0, "CODE", "");
	ds_buj.SetColumn(0, "CODE_NAME", "- 선택 -");
	cb_buj.Index = 0;
}

function cb_juj_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if(trim(cb_juj.Value) == trim(cb_buj.Value)) {
		var arr = [Static5.value, Static6.value];
		gfn_showAlert("CW00025", arr);
		return;
	}
}

function cb_buj_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if(trim(cb_juj.Value) == trim(cb_buj.Value)) {
		var arr = [Static5.value, Static6.value];
		gfn_showAlert("CW00025", arr);
		return;
	}
}

/********************************************************************************
* 기      능   : 입력
********************************************************************************/
function fn_input() {

	if(fn_saveCheck() == true) {

		// Message 영역 초기화
		global.FRM_BOTTOM.img_msgType.imageId = "";
		global.FRM_BOTTOM.st_msg.value = "";

		ds_list.fireevent = false;
		gfn_copyDs(ds_list, ds_list_0, "CHECK=1");
		ds_list.fireevent = true;

		// 선택된 데이터가 없으면 에러 
		if (ds_list_0.rowcount < 1) {
		gfn_showAlert("선택된 데이터가 없습니다.");
		return;
		}	

		var arg = "fa_bsu1=" + quote(cb_bsu1.value)		//이관후보수업체:지역코드
				+ " fa_bsu2=" + quote(cb_bsu2.value)	//이관후보수업체:업체코드
				+ " fa_bsu1t=" + quote(cb_bsu1.text)	//이관후보수업체:지역명
				+ " fa_bsu2t=" + quote(cb_bsu2.text)	//이관후보수업체:업체명
				+ " fa_juj=" + quote(cb_juj.value)		//이관후보수업체:주점검자코드
				+ " fa_buj=" + quote(cb_buj.value)		//이관후보수업체:부점검자코드
				+ " fa_jujt=" + quote(cb_juj.text)		//이관후보수업체:주점검자명
				+ " fa_bujt=" + quote(cb_buj.text)		//이관후보수업체:부점검자명
				+ " fa_igm=" + quote(med_yymm.Value)		//이관 월
				+ " fa_year=" + quote(substr(med_yymm.Value,0,4))		//이관대상년월:년
				//--------------------------------------------------------------------------
				//+ " fa_month=" +quote(substr(med_yymm.Value,5,2));	//이관대상년월:월
				//--------------------------------------------------------------------------
				+ " fa_month=" +quote(substr(med_yymm.Value,4,2));	// 월 생성 시, 앞 자릿수 잘리는 버그로 인해 수정. 2018.11.16 J.H Han

		gfn_openPopup("CS1403001B", true, arg);	
	}	
}


/********************************************************************************
* 기      능   :  그리드 더블 클릭 
********************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	// Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	var docNo = ds_list.GetColumn(nRow,"DOCNO");

	if (docNo==null || length(trim(docNo))==0) return;

	var arg = "fa_bsu1=" + quote(cb_bsu1.value)		//이관후보수업체:지역코드
			+ " fa_bsu2=" + quote(cb_bsu2.value)	//이관후보수업체:업체코드
			+ " fa_bsu1t=" + quote(cb_bsu1.text)	//이관후보수업체:지역명
			+ " fa_bsu2t=" + quote(cb_bsu2.text)	//이관후보수업체:업체명
			+ " fa_juj=" + quote(cb_juj.value)		//이관후보수업체:주점검자코드
			+ " fa_buj=" + quote(cb_buj.value)		//이관후보수업체:부점검자코드
			+ " fa_jujt=" + quote(cb_juj.text)		//이관후보수업체:주점검자명
			+ " fa_bujt=" + quote(cb_buj.text)		//이관후보수업체:부점검자명
			+ " fa_year=" + quote(substr(med_yymm.Value,0,4))		//이관대상년월:년
			+ " fa_month=" + quote(substr(med_yymm.Value,5,2))	//이관대상년월:월
			+ " fa_docno=" + quote(docNo);
	gfn_openPopup("CS1403001B", true, arg);	
}

/********************************************************************************
* 기      능   : 온핸드 현장 체크
********************************************************************************/
function fn_isOnHandChk(idx) {
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs14:CS1403001A_S15");

	ds_isonhand_cond.ClearData();
	ds_isonhand_cond.AddRow();

	ds_isonhand_cond.SetColumn(0, "PJT", ds_list.GetColumn(idx, "PJT"));
	ds_isonhand_cond.SetColumn(0, "HNO", ds_list.GetColumn(idx, "HNO"));

	tit_callService(
		""
		, ""
		, "ds_isonhand_cond=ds_isonhand_cond"
		, "ds_isonhand_result=ds_isonhand_result"
		, ""
		, ""
		, true
		, true
		, true
		);

	if(ds_isonhand_result.GetColumn(0,"CNT") > 0) {
		var upn_cst = ds_isonhand_result.GetColumn(0,"UPN") + "-" + ds_isonhand_result.GetColumn(0,"CST");
		var pjt_hno = ds_isonhand_result.GetColumn(0,"PJT") + ds_isonhand_result.GetColumn(0,"HNO");
		gfn_showAlert("온핸드 현장이 존재합니다.\n거래선 : " + upn_cst + " / " + "원프로젝트 : " + pjt_hno + "\n" + "거래선을 다시 확인 바랍니다.");
		return false;
	}

	return true;
}
