function fn_OnLoadCompleted(obj) {

	gfn_initForm();	
	var user_grp = gfn_getUserGroup();	

	//공통코드
/*	사용안함
	var codeArr = ["CS115"];	
	var dsObjArr = [ds_bsu1];
	var addTypeArr = [G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);	
*/
	parent.ds_list_0;
	//trace(parent.ds_list_0.saveXml());

	// 2019.01.07 개별점검이관 결제완료 시 강제승인 권한 가져오기
	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS1403001B");
	ds_rcond.SetColumn(0, "GBN", "01");	
	seungin_auth = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();

	if(seungin_auth == true) { // 강제승인 권한이 존재하면 승인버튼 활성화 처리.
		btn_admin.Visible = true;
		btn_admin.Enable = true;
	}

	ds_cond_req.setColumn(0, "DOCNO", fa_docno);

	//============== 수신부서 결재그룹 체크 ===============================	
	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "WBAP_DOCKIND");
	ds_rcond.SetColumn(0, "GBN", "01");	
	vDPT = gfn_getAuthDpt(ds_rcond);
	ds_rcond.ClearData();
    //=========================================================   

	//============== 원복버트 체크 ===============================	
	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS1403001B");
	ds_rcond.SetColumn(0, "GBN", "02");	
	rtn_auth = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();
    //=========================================================       	



	if(length(ds_cond_req.getColumn(0, "DOCNO")) > 0) {     //결재 상세창
		g_docno = fa_docno;
		fn_reqDoc();
	} else {   												//입력 상세창
		fn_docgen(); 
		fn_upn();    
	}
}

