/********************************************************************************
* 기      능   : 팝업할때 세팅
********************************************************************************/
function fn_query() {
	// 이관 후 설정

	// 사용자에게 입력받은 월과 현재일자의 월을 비교하여 예외처리하는 로직 추가. 2018.11.16 J.H Han
	var curYear = substr(gfn_getCurrDate(),0,4);   // 시스템일자 년도
	var curMonth = substr(gfn_getCurrDate(),4,2);  // 시스템일자 월
	var curDateTime = curYear + curMonth;          // 시스템일자 년월

	if(curDateTime < fa_year + fa_month) { // 현재일자보다 사용자에게 입력받은 일자가 크면. Ex) 2018.12(현재일자) / 2019.01(사용자입력일자)
		gfn_showAlert("현재월 이후로 개별점검이관 작업을 처리할수 없습니다." + "\n" + "서비스관리부에 문의해주세요.");
		close();
	}

	g_docno = ds_docno_gen.GetColumn(0, "DOCNO");
	ed_docno.Text = ds_docno_gen.GetColumn(0, "DOCNO");
	ds_after.setColumn(0, "BSU1", fa_bsu1);
	ds_after.setColumn(0, "LGORTAFTERTEXT", fa_bsu2t);
	ds_after.setColumn(0, "JUJ", fa_juj);
	ds_after.setColumn(0, "BUJ", fa_buj);
	ds_after.setColumn(0, "LGORTAFTER", fa_bsu2);
	ds_after.setColumn(0, "YEAR", fa_year);
	ds_after.setColumn(0, "MONTH", fa_month); 
	ds_after.setColumn(0, "BSU1T", fa_bsu1t);
	ds_after.setColumn(0, "BSU2T", fa_bsu2t);
	ds_after.setColumn(0, "JUJT", fa_jujt);
	ds_after.setColumn(0, "BUJT", fa_bujt);

	//trace(ds_after.SaveXML());

	bsu_count.value = ds_list_0.rowcount(); 

	// 이전업체 보수범위,관리대수

	ds_cond.SetColumn(0, "BSU", ds_list_0.GetColumn(0, "BSU"));
	fn_beforebsu_start();	

	// 팀

		if (cbafter_bsu1.value == "") {
			ds_cond.SetColumn(0, "BSU", ds_after.GetColumn(0, "LGORTAFTER"));	
			tit_clearActionInfo();
			tit_addSearchActionInfo("cs14:CS1403001B_S11");		
			// tit_callService(
				// ""
				// , ""
				// , "ds_cond=ds_cond"
				// , "ds_tem=ds_tem"
				// , ""
				// , "fn_afterbsu2"
				// , true); 
			tit_callService(
				""
				, ""
				, "ds_cond=ds_cond"
				, "ds_tem=ds_tem"
				, ""
				, "fn_afterbsu2"
				, true
				, ""
				, true); 	


		} else {
			return;
		}	
}

