}

/********************************************************************************
* 기      능   : 이관전 업체 보수범위, 변경전 후 관리대수,차이 세팅
********************************************************************************/
function fn_beforebsu() {		
	// 주차의 경우 관리대수 데이타 없음 모두 0 으로 셋팅
	if( ds_list_re.RowCount() < 1) {
		bscp.value = 0 ;
		bto1.value  = 0 ;
		bsu_count.value  = 0 ;
	} else {
		bscp.value = ds_list_re.GetColumn(0,"SCP");
		bto1.value = ds_list_re.GetColumn(0,"TOTAL");
	}
	var bscpi = parseInt(bscp.value); //보수범위
	var bto1i = parseInt(bto1.value);	//변경전 관리대수
	var varc = parseInt(bsu_count.value);	//대수
	bto2.value = bto1i - varc;	//뱐경후 괸리대수 = 변경전 관리대수 - 이관대수
	var bto2i = parseInt(bto2.value);
	if (bscp.value >0) {
		bgap1.value = bscpi - bto1i;	//변경전 차이 = 보수범위-변경전 관리대수
		bgap2.value = bscpi - bto2i;	//변경후 차이 = 보수범위-변경후 관리대수
	} else {// 주차의 경우 
		bgap1.value  = 0 ;	
		bgap2.value  = 0 ;	
	}

	// 이후업체 보수범위,관리대수2

	ds_cond.SetColumn(0, "BSU", ds_after.GetColumn(0, "LGORTAFTER"));	
	fn_afterbsu_start();		
}

function fn_afterbsu() {
	// 주차의 경우 관리대수 데이타 없음 모두 0 으로 셋팅
	if( ds_list_re.RowCount() < 1) {
		ascp.value = 0 ;
		ato1.value  = 0 ;
		bsu_count.value  = 0 ;
	} else {				
		ascp.value = ds_list_re.GetColumn(0,"SCP");
		ato1.value = ds_list_re.GetColumn(0,"TOTAL");	
	}	
		var ascpi = parseInt(ascp.value);
		var ato1i = parseInt(ato1.value);
		var varc = parseInt(bsu_count.value);
		ato2.value = ato1i + varc;	
		var ato2i = parseInt(ato2.value);	
		if (ascp.value >0) {
			agap1.value = ascpi - ato1i;	
			agap2.value = ascpi - ato2i;
		} else {// 주차의 경우 
		agap1.value  = 0 ;	
		agap2.value  = 0 ;	
		}
}


function fn_afterbsu2() {
		ds_after.setColumn(0, "TEM", ds_tem.GetColumn(0,"TEM"));
		ds_after.setColumn(0, "TEM_NM", ds_tem.GetColumn(0,"TEM_NM"));	
		//ds_after.setColumn(0, "MONTH", fa_month);

		cbbefore_bsu1.Value = Substr(bbsu1.Value, 0, 6);
		cbbefore_bsu2.Value = bbsu2.Value;
		cbafter_bsu1.Value = Substr(absu1.Value, 0, 6);
		cbafter_bsu2.Value = absu2.Value;
}


