function fn_afterprint() {

		ds_print.SetColumn(0,"DOCNO",ed_docno.Text);
		ds_print.SetColumn(0,"TEM_AFTER",absu1.value);
		ds_print.SetColumn(0,"BSU_AFTER",absu2.value);
		ds_print.SetColumn(0,"DATE_REQ",date_req.value);
		ds_print.SetColumn(0,"PJT",ed_pjt1.value);
		ds_print.SetColumn(0,"SPT",ed_vkgrp.value);
		ds_print.SetColumn(0,"BSU_COUNT",bsu_count.value);
		ds_print.SetColumn(0,"CST",ds_list_0.GetColumn(0, "CST"));
		ds_print.SetColumn(0,"USD",ds_list_amt.GetColumn(0, "USD"));
		ds_print.SetColumn(0,"UMR",ds_list_amt.GetColumn(0, "UMR"));
		ds_print.SetColumn(0,"GBN1",ds_list_amt.GetColumn(0, "GBN1"));
		ds_print.SetColumn(0,"AMT",ds_list_amt.GetColumn(0, "AMT"));

		var amt = parseInt(ds_list_amt.GetColumn(0, "AMT"));
		var ho = parseInt(ds_list_amt.GetColumn(0, "CNT"));
		var amtper = amt/ho ;	

		ds_print.SetColumn(0,"AMT_PER",amtper);
		ds_print.SetColumn(0,"HST_NM",ds_list_0.GetColumn(0, "HST_NM"));
		ds_print.SetColumn(0,"REASON2",cb_reason2.text);   
		ds_print.SetColumn(0,"TEM_BEFO",bbsu1.value);
		ds_print.SetColumn(0,"BSU_BEFO",bbsu2.value);
		ds_print.SetColumn(0,"JUJ_BEFO",cbbefore_juj.text);
		ds_print.SetColumn(0,"BUJ_BEFO",cbbefore_buj.text);
		ds_print.SetColumn(0,"JUJ_AFTER",cbafter_juj.text);
		ds_print.SetColumn(0,"BUJ_AFTER",cbafter_buj.text);
		ds_print.SetColumn(0,"REQE",tx_reqe.value);
		ds_print.SetColumn(0,"REQC",tx_reqc.value);
		ds_print.SetColumn(0,"REQ",tx_req.value);
		ds_print.SetColumn(0,"D_TEM",ds_draftpers_list.GetColumn(0,"D_TEM"));
		ds_print.SetColumn(0,"D_RANK",ds_draftpers_list.GetColumn(0,"D_RANK"));
		ds_print.SetColumn(0,"D_NAME",ds_draftpers_list.GetColumn(0,"D_NAME"));
		ds_print.SetColumn(0,"D_DATE",ds_draftpers_list.GetColumn(0,"D_DATE"));
		ds_print.SetColumn(0,"D_TOTEM","서비스 관리부");

		// UbiReport Call
		Dialog("CS14::CS1403001C.xml", "");

}
