// 사유
function fn_reqNullcheck() {

	if(length(trim(cb_reason2.Value)) == 0) {
		gfn_showAlert("CW00001","사유");
		return false;
	}
	if(length(trim(date_req.Value)) == 0) {
		gfn_showAlert("CW00001","작성일");
		return false;
	}
	if(length(trim(tx_reqe.Value)) == 0) {
		gfn_showAlert("CW00001","당사요구안");
		return false;
	}
	if(length(trim(tx_reqc.Value)) == 0) {
		gfn_showAlert("CW00001","고객요구안");
		return false;
	}
	if(length(trim(tx_req.Value)) == 0) {
		gfn_showAlert("CW00001","협의결과");
		return false;
	}	

	return true;
}

/********************************************************************************
* 기      능   : 기안된 문서 확인 상세
********************************************************************************/		
function fn_reqDoc() {

	ds_list_aps.setColumn(0, "DOCNO", ds_cond_req.getColumn(0, "DOCNO"));
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs14:CS1403001B_S17");		
	tit_callService(
		""
		, ""
		, "ds_list_aps=ds_list_aps"
		, "ds_list_aps=ds_list_aps"
		, ""							
		, ""							
		, true							
		, false							
		, true); //동기화 방식으로 서비스를 콜한다.


	if (ds_list_aps.GetColumn(0,"APSTAT") == "2" || ds_list_aps.GetColumn(0,"APSTAT") == "5") {
		fn_reqDoc2();  // 반송된 상세정보
	} else { 
		fn_reqDoc1();
	}
}

