function fn_afterReqDoc1() {
	//******************** 버튼 활성화
	//if (parseInt(ds_list_req.GetColumn(0,"APSTAT")) < 1 || (ds_list_req.GetColumn(0,"APSTAT") == "3" && ds_list_req.GetColumn(0,"REDPT1") == G_DPT1)) {
	if (parseInt(ds_list_req.GetColumn(0,"APSTAT")) < 1 || (ds_list_req.GetColumn(0,"APSTAT") == "3" && ds_list_req.GetColumn(0,"REDPT1") == vDPT)) {
		div_btn.btn_draft.Enable = true; 
		div_btn.btn_sendback.Enable = false;
		div_btn.btn_decide.Enable = false;
	} else if((ds_list_req.GetColumn(0,"APSTAT") > '0' && ds_list_req.GetColumn(0,"APSTAT") != '3' && ds_list_req.GetColumn(0,"APSTAT") < '6') && G_USER_ID == substr(ds_decide.GetColumn(0, "APNUM"),1) ) {
		div_btn.btn_draft.Enable   = false;	
		div_btn.btn_sendback.Enable = true;	
		div_btn.btn_decide.Enable = true;
	} else {
		div_btn.btn_decide.Enable = false;
		div_btn.btn_sendback.Enable = false;
		div_btn.btn_draft.Enable = false;

		//===========================원복 버튼 활성화===========================
		if(rtn_auth == true) {
			btn_return.Enable = true;
		}
		//======================================================================

	}

	//******************** 화면세팅
	cb_reason2.Enable = false;
	date_req.Enable = false;
	tx_reqe.Enable = false;
	tx_reqc.Enable = false;
	tx_req.Enable = false;

	ed_docno.Value = ds_list_req.GetColumn(0, "DOCNO");
	ed_gbn1.Value = ds_list_req.GetColumn(0, "CLI");
	ed_vkgrp.Value = ds_list_req.GetColumn(0, "SPT");
	ed_pjt1.Value = ds_list_req.GetColumn(0, "PJT");
	bsu_count.Value = ds_list_req.rowcount();
	cbbefore_juj.Value = ds_list_req.GetColumn(0, "JUB_NM");
	cbbefore_buj.Value = ds_list_req.GetColumn(0, "BUB_NM");
	cbafter_juj.Value = ds_list_req.GetColumn(0, "JUA_NM");
	cbafter_buj.Value = ds_list_req.GetColumn(0, "BUA_NM");

	bbsu1.Value = ds_list_req.GetColumn(0, "TEMB_NM");
	bbsu2.Value = ds_list_req.GetColumn(0, "BSUB_NM");
	absu1.Value = ds_list_req.GetColumn(0, "TEMA_NM");
	absu2.Value = ds_list_req.GetColumn(0, "BSUA_NM");

	cbbefore_bsu1.Value = Substr(bbsu1.Value, 0, 6);
	cbbefore_bsu2.Value = bbsu2.Value;
	cbafter_bsu1.Value = Substr(absu1.Value, 0, 6);
	cbafter_bsu2.Value = absu2.Value;

	cb_reason2.Text = ds_list_req.GetColumn(0, "RSN");

	var date_igm = ds_list_req.GetColumn(0, "IGM");	
	date_y.Value = Substr(date_igm, 0, 4);
	date_m.Value = Substr(date_igm, 4, 2);

	date_req.Value = ds_list_req.GetColumn(0, "IKD");

	tx_reqe.Value = ds_list_req.GetColumn(0, "REQE");
	tx_reqc.Value = ds_list_req.GetColumn(0, "REQC");
	tx_req.Value = ds_list_req.GetColumn(0, "REQ");

	ds_after.SetColumn(0, "LGORTAFTER", ds_list_req.GetColumn(0, "CHA"));
	ds_cond.SetColumn(0, "BSU", ds_list_req.GetColumn(0, "CHB"));
	fn_beforebsu_start();
}

function fn_afterReqDoc2() {    // 반송된 상세정보
	//******************** 버튼 활성화
	div_btn.btn_decide.Enable = false;
	div_btn.btn_sendback.Enable = false;
	div_btn.btn_draft.Enable = false;
	div_btn.btn_print.Enable = false;

	//******************** 화면세팅
	cb_reason2.Enable = false;
	date_req.Enable = false;
	tx_reqe.Enable = false;
	tx_reqc.Enable = false;
	tx_req.Enable = false;

	//alert("ds_list_req.GetColumn(0, 'JUB_NM';"+ds_list_req.GetColumn(0, "JUB_NM"));

	ed_docno.Value = ds_list_req.GetColumn(0, "DOCNO");
	ed_gbn1.Value = ds_list_req.GetColumn(0, "CLI");
	ed_vkgrp.Value = ds_list_req.GetColumn(0, "SPT");
	ed_pjt1.Value = ds_list_req.GetColumn(0, "PJT");
	bsu_count.Value = ds_list_req.GetColumn(0, "CNT");
	cbbefore_juj.Value = ds_list_req.GetColumn(0, "JUB_NM");
	cbbefore_buj.Value = ds_list_req.GetColumn(0, "BUB_NM");
	cbafter_juj.Value = ds_list_req.GetColumn(0, "JUA_NM");
	cbafter_buj.Value = ds_list_req.GetColumn(0, "BUA_NM");

	bbsu1.Value = ds_list_req.GetColumn(0, "TEMB_NM");
	bbsu2.Value = ds_list_req.GetColumn(0, "BSUB_NM");
	absu1.Value = ds_list_req.GetColumn(0, "TEMA_NM");
	absu2.Value = ds_list_req.GetColumn(0, "BSUA_NM");

	cbbefore_bsu1.Value = Substr(bbsu1.Value, 0, 6);
	cbbefore_bsu2.Value = bbsu2.Value;
	cbafter_bsu1.Value = Substr(absu1.Value, 0, 6);
	cbafter_bsu2.Value = absu2.Value;

	cb_reason2.Text = ds_list_req.GetColumn(0, "RSN");

	var date_igm = ds_list_req.GetColumn(0, "IGM");	
	date_y.Value = Substr(date_igm, 0, 4);
	date_m.Value = Substr(date_igm, 4, 2);

	date_req.Value = ds_list_req.GetColumn(0, "IKD");
	bscp.Value = ds_list_req.GetColumn(0, "CPB");
	ascp.Value = ds_list_req.GetColumn(0, "CPA");
	bto1.Value = ds_list_req.GetColumn(0, "BTOB");
	ato1.Value = ds_list_req.GetColumn(0, "BTOA");

	tx_reqe.Value = ds_list_req.GetColumn(0, "REQE");
	tx_reqc.Value = ds_list_req.GetColumn(0, "REQC");
	tx_req.Value = ds_list_req.GetColumn(0, "REQ");

	var bscpi = parseInt(bscp.value);
	var bto1i = parseInt(bto1.value);
	var varc = parseInt(bsu_count.value);
	bto2.value = bto1i - varc;
	var bto2i = parseInt(bto2.value);
	if (bscp.value >0) {
		bgap1.value = bscpi - bto1i;
		bgap2.value = bscpi - bto2i;
	}
	var ascpi = parseInt(ascp.value);
	var ato1i = parseInt(ato1.value);
	var varc = parseInt(bsu_count.value);
	ato2.value = ato1i + varc;	
	var ato2i = parseInt(ato2.value);	
	if (ascp.value >0) {
		agap1.value = ascpi - ato1i;	
		agap2.value = ascpi - ato2i;
	}

}
/********************************************************************************
* 기      능   : 기안 
********************************************************************************/
function fn_draft() {
	if(fn_reqNullcheck() == true) {
		gfn_clearMsgBar();

		// 자회사 로직
		// 자회사는 결재를 진행하지 않기 때문에 기안시에 결재까지 처리한다.
		// 결재정보는 강제로 생성
		var vVkbur = gfn_getBsu(ds_list_0.getColumn(0,"LGORTBEFORE"), "VKBUR");
		if (vVkbur == "H100"){
			ds_wbap01.ClearData();
			ds_wbap02.ClearData();
			ds_wbap01.AddRow();
			ds_wbap01.SetColumn(0, "DOCNO"   , ed_docno.Text);
			ds_wbap01.SetColumn(0, "DOCKIND" , "8");

			ds_wbap02.AddRow();
			ds_wbap02.SetColumn(0, "DOCNO"   , ed_docno.Text);
			ds_wbap02.SetColumn(0, "DOCKIND" , "8");

			// 결재정보 공통함수
			gfn_setZwbap(ds_wbap01, ds_wbap02, true);
			// 자료 저장
			ds_cond_req.setColumn(0, "DOCNO", ed_docno.Text);
			fn_save();
			// 최종결재처리
			fn_beApproval();

		} else {
			var appgubn = "S";
			if(ds_list_req.GetColumn(0, "APSTAT") == "3") {
				appgubn = "R";
			}

			var arg = "fa_docno=" + quote(ed_docno.Text)
					+ " fa_dockind=8" 
					+ " fa_appgubn=" + quote(appgubn);

			var rtn = gfn_openPopup2("CS0401001A", true, arg);

			if( rtn[0] == "S" ) {
			//	gfn_showAlert("CI00003");
				gfn_showMsg("CI00003", '전자결재문서번호:' + ed_docno.Text);
				div_btn.btn_draft.Enable = false;

				ds_cond_req.setColumn(0, "DOCNO", ed_docno.Text);  //결재번호 세팅

				if (parseInt(ds_list_req.GetColumn(0, "APSTAT")) < 1) {	//최초기안처리시
					fn_save();
				} else {
					fn_reqDoc();			
				}			
			} else {
			//	gfn_showAlert("CE00001"); 
				gfn_showMsg("CE00001");
			}	
		}
	}
}

function fn_save() {

		//ZCST157
		ds_insert_m.ClearData();

		var j = 0;
		for(var i=0; i<ds_list_0.RowCount(); i++) {	
			ds_insert_m.AddRow();
			ds_insert_m.SetColumn(j, "UPN", ds_list_0.GetColumn(i, "UPN"));
			ds_insert_m.SetColumn(j, "CST", ds_list_0.GetColumn(i, "CST"));
			ds_insert_m.SetColumn(j, "PJT", ds_list_0.GetColumn(i, "PJT"));
			ds_insert_m.SetColumn(j, "HNO", ds_list_0.GetColumn(i, "HNO"));
			ds_insert_m.SetColumn(j, "IGM", fa_igm);
			ds_insert_m.SetColumn(j, "BALJUCODE", ds_list_0.GetColumn(i, "BALJUCODE"));
			ds_insert_m.SetColumn(j, "SEQ", ds_list_0.GetColumn(i, "SEQ"));
			ds_insert_m.SetColumn(j, "LGORTBEFORE", ds_list_0.GetColumn(i, "LGORTBEFORE"));
			ds_insert_m.SetColumn(j, "AGB", ds_list_0.GetColumn(i, "AGB"));
			ds_insert_m.SetColumn(j, "GHB", ds_list_0.GetColumn(i, "GHB"));
			ds_insert_m.SetColumn(j, "LGORTAFTER", ds_after.GetColumn(0, "LGORTAFTER"));
			ds_insert_m.SetColumn(j, "GHA", ds_list_0.GetColumn(i, "GHA"));
			ds_insert_m.SetColumn(j, "JUA", ds_after.GetColumn(0, "JUJ"));
			ds_insert_m.SetColumn(j, "BUA", ds_after.GetColumn(0, "BUJ"));
			ds_insert_m.SetColumn(j, "IHT", ds_list_0.GetColumn(0,"IHT"));
			ds_insert_m.SetColumn(j, "RSN", cb_reason2.Text);
			ds_insert_m.SetColumn(j, "DOCNO", ds_docno_gen.GetColumn(0, "DOCNO"));
			ds_insert_m.SetColumn(j, "TEMB", ds_list_0.GetColumn(i, "TEM"));
			ds_insert_m.SetColumn(j, "TEMA", ds_after.GetColumn(0, "TEM"));
			ds_insert_m.SetColumn(j, "FYN", ds_list_0.GetColumn(i, "CS157_FYN"));

			j++;
		}

		//ZCST158
		ds_insert_s.SetColumn(0, "DOCNO", ds_docno_gen.GetColumn(0, "DOCNO"));
		ds_insert_s.SetColumn(0, "REQE", tx_reqe.value);
		ds_insert_s.SetColumn(0, "REQC", tx_reqc.value);
		ds_insert_s.SetColumn(0, "REQ", tx_req.value);

		//ZCST643
		ds_insert_s2.ClearData();

		var j = 0;
		for(var i=0; i<ds_list_0.RowCount(); i++) {	
			ds_insert_s2.AddRow();
			ds_insert_s2.SetColumn(j, "DOCNO", ds_docno_gen.GetColumn(0, "DOCNO"));
			ds_insert_s2.SetColumn(j, "UPN", ds_list_0.GetColumn(i, "UPN"));
			ds_insert_s2.SetColumn(j, "CST", ds_list_0.GetColumn(i, "CST"));
			ds_insert_s2.SetColumn(j, "PJT", ds_list_0.GetColumn(i, "PJT"));
			ds_insert_s2.SetColumn(j, "HNO", ds_list_0.GetColumn(i, "HNO"));
			ds_insert_s2.SetColumn(j, "IGM", fa_igm);
			ds_insert_s2.SetColumn(j, "IKD", date_req.value);
			ds_insert_s2.SetColumn(j, "CLI", ed_gbn1.Text);
			ds_insert_s2.SetColumn(j, "SPT", ed_vkgrp.Text);
			ds_insert_s2.SetColumn(j, "CNT", bsu_count.value);
			ds_insert_s2.SetColumn(j, "TEMB", bbsu1.Text);
			ds_insert_s2.SetColumn(j, "TEMA", absu1.Text);
			ds_insert_s2.SetColumn(j, "BSUB", bbsu2.Text);
			ds_insert_s2.SetColumn(j, "BSUA", absu2.Text);
			ds_insert_s2.SetColumn(j, "JUB", cbbefore_juj.Text);
			ds_insert_s2.SetColumn(j, "JUA", cbafter_juj.Text);
			ds_insert_s2.SetColumn(j, "BUB", cbbefore_buj.Text);
			ds_insert_s2.SetColumn(j, "BUA", cbafter_buj.Text);	
			ds_insert_s2.SetColumn(j, "CPB", parseInt(bscp.value));
			ds_insert_s2.SetColumn(j, "CPA", parseInt(ascp.value));
			ds_insert_s2.SetColumn(j, "BTOB", bto1.value);
			ds_insert_s2.SetColumn(j, "BTOA", ato1.value);
			ds_insert_s2.SetColumn(j, "RSN", cb_reason2.Text);

			j++;
		}

		tit_clearActionInfo();
		tit_addMultiActionInfo("cs14:CS1403001B_I1");		//ZCST157
		tit_addMultiActionInfo("cs14:CS1403001B_I2");		//ZCST158
		tit_addMultiActionInfo("cs14:CS1403001B_I3");		//ZCST643

		tit_callService(
		""
		, ""
		, "ds_insert_m=ds_insert_m ds_insert_s=ds_insert_s ds_insert_s2=ds_insert_s2"
		, "" 
		, ""
		, "fn_afterSave");
}

/********************************************************************************
* 기      능   : 결재, 반송  Callback
********************************************************************************/
function fn_afterDecide(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		div_btn.btn_decide.Enable = false;		// 결재버튼
		div_btn.btn_draft.Enable  = false;		// 기안버튼
	//	div_btn.btn_print.Enable  = false;		// 출력버튼
		div_btn.btn_sendback.Enable = false; 	// 반송버튼	
	}

	fn_beApproval();
}
/********************************************************************************
* 기      능   : 최종결재자 결재후 승인처리
********************************************************************************/
function fn_beApproval() {
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs14:CS1403001B_S13");
	tit_callService(
	""
	, ""
	, "ds_cond_req=ds_cond_req"
	, "ds_list_req=ds_list_req"
	, ""
	, "fn_chkApproval"
	, false
	, true);
}

function fn_chkApproval() {	
	if (ds_list_req.GetColumn(0,"APSTAT") == "6") {
		ufn_approval();
	} else if (ds_list_req.GetColumn(0,"APSTAT") == "2" || ds_list_req.GetColumn(0,"APSTAT") == "5") {
		ufn_afterSendback();
	}  else {
		return;
	}
}

function ufn_approval() {
	gfn_clearMsgBar();

	ds_list_req.SetColumn(0, "FLAG", "U");
	ds_list_req.SetColumn(0, "FLAG", "U");

	tit_clearActionInfo();
	tit_callService(
		"CS1403001A_ACT"
		, "save"
		, "ds_list=ds_list_req"
		, ""
		, ""
		, "fn_afterApproval"); 
}

function ufn_afterSendback() {
	gfn_clearMsgBar();

	tit_clearActionInfo();
	tit_addMultiActionInfo("cs14:CS1403001B_D1");
	tit_callService(
	""
	, ""
	, "ds_list=ds_cond_req"
	, ""
	, ""
	, "fn_afterSendbak"
	, true
	, true);
}

function fn_afterApproval(errCode, errMsg) {
	if(errCode != 0) {
		rollbackDecide();
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_reqDoc();
	}
}

function fn_afterSendbak(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_reqDoc();
	}
}

function rollbackDecide() {
	tit_clearActionInfo();

	ds_head_0.ClearData();
	ds_head_0.AddRow();
	ds_head_0.SetColumn(0, "DOCNO", g_docno);
	ds_head_0.SetColumn(0, "DOCKIND", "8");

	tit_addMultiActionInfo("cs04:CS0401002A_U6"); //결재상태 복구 ZWBAP01
	tit_addMultiActionInfo("cs04:CS0401002A_U7"); //결재상태 복구 ZWBAP02
	tit_callService( 
		  ""
		, ""
		, "ds_list_0=ds_head_0" 
		, ""
		, ""
		, ""
		, true
		, true);
}

function deleteDecide(){
	tit_clearActionInfo();

	ds_head_0.ClearData();
	ds_head_0.AddRow();
	ds_head_0.SetColumn(0, "DOCNO", g_docno);
	ds_head_0.SetColumn(0, "DOCKIND", "8");

	tit_addMultiActionInfo("cs04:CS0401002A_D1"); 
	tit_addMultiActionInfo("cs04:CS0401002A_D2"); 
	tit_callService( 
		  ""
		, ""
		, "ds_head_0=ds_head_0" 
		, ""
		, ""
		, ""
		, true
		, true);
}

/********************************************************************************
