	fn_beApproval();
}


/********************************************************************************
* 기      능   : 이전상태 원복
********************************************************************************/
function btn_return_OnClick(obj)
{

	if (trim(ds_cond_req.GetColumn(0, "DOCNO")) == '')
	{
		gfn_showAlert("문서번호가 없는 시스템 이관은 원복이 불가합니다.");
		return;
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs14:CS1403001B_S18");
	tit_addSearchActionInfo("cs14:CS1403001B_S19");
	//결재정보 확인
	tit_callService(
        ""
        , ""
        , "ds_cond_req=ds_cond_req"
        , "ds_wbap01_req=ds_wbap01_req ds_cs157_cnt=ds_cs157_cnt"
        , ""
        , ""
        , ""
        , ""
        , true);

	if (ds_wbap01_req.GetColumn(0, "APSTAT") != '6')
	{
		gfn_showAlert("현재문저는 결재 진행중 or 반송 or 시스템 이관 문서입니다.");
		return;
	}

	if (ds_cs157_cnt.GetColumn(0, "CNT") > 0)
	{
		gfn_showAlert("선택하신 문서의 호기중 문서의 이관월 이후에 이관 내역이 존재합니다.");
		return;
	}	


	ds_list_req.Filter("FindRow('PJT',PJT)=row");	
	ds_list_pspid.ClearData();
	ds_list_pspid.CopyF(ds_list_req);	
	ds_list_req.UnFilter();




	tit_clearActionInfo();
 	tit_addMultiActionInfo("cs14:CS1403001B_U1");
 	tit_addMultiActionInfo("cs14:CS1403001B_U2");
 	tit_addMultiActionInfo("cs14:CS1403001B_U3");
 	tit_addMultiActionInfo("cs14:CS1403001B_U4");
    tit_addMultiActionInfo("cs14:CS1403001B_U5");
    tit_addMultiActionInfo("cs14:CS1403001B_U6");
    tit_addMultiActionInfo("cs14:CS1403001B_U7");
    tit_addMultiActionInfo("cs14:CS1403001B_U8");

	tit_callService(
		""
		, ""
		, "ds_list_pspid=ds_list_pspid ds_list_req=ds_list_req"
		, ""
		, ""
		, "fn_afterSave2"
		, ""
		, ""
		, true);



}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave2(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		//gfn_showMsg("CI00003");
		fn_req_del();
	}
}


/********************************************************************************
* 기      능   : 원복 업데이트 성공후 삭제 처리
********************************************************************************/		
function fn_req_del() {

	tit_clearActionInfo();
	tit_addMultiActionInfo("cs14:CS1403001B_D2");
	tit_addMultiActionInfo("cs14:CS1403001B_D3");
	tit_addMultiActionInfo("cs14:CS1403001B_D4");

	tit_callService(
	""
	, ""
	, "ds_cond_req=ds_cond_req"
	, ""
	, ""
	, "fn_after_req_del"
	, ""
	, ""
	, true);

}


/********************************************************************************
* 기      능   : 삭제 Callback  
********************************************************************************/
function fn_after_req_del(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00005");
		parent.fn_query();
		close();

	}
}
