var user_grp;
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) 
{
	gfn_initForm(true);

	tab.TabIndex=0;	
	user_grp = gfn_getUserGroup();
	ds_cond.SetColumn(0,"USER_GROUP",user_grp);

	if(user_grp=="20" || user_grp=="30" || user_grp=="40")
	{
		tab.tab2.btn_plus.Enable = true;
		tab.tab2.btn_minus.Enable = true;
//		ed_spt.Enable=true;
//		ed_nam.Enable=true;			

		if(tab.TabIndex==0)
		{		
//			ufn_enableBtn("save",true);
			ufn_enableBtn("confirm",false);
			ufn_enableBtn("cancel",false);		
		}
		else if(tab.TabIndex==1)
		{		
//			ufn_enableBtn("save",false);
			ufn_enableBtn("confirm",true);
			ufn_enableBtn("cancel",true);		
		}		
	}
	else if(user_grp=="10")
	{
		tab.tab2.btn_plus.Enable = false;
		tab.tab2.btn_minus.Enable = false;
//		ed_spt.Enable = false;
//		ed_nam.Enable = false;
//		ufn_enableBtn("save",false);
		ufn_enableBtn("confirm",false);
		ufn_enableBtn("cancel",false);		
	}
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) 
{
	return gfn_isFormUnload(obj);
}
