/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() 
{	
	if(ds_cond.GetColumn(0,"UPN")=="")
	{
		//alert("통합Pjt No를 입력하세요.");	
		gfn_showAlert("CW00001","통합Pjt No.");
	}
	else
	{	
		tit_clearActionInfo();

		if(tab.TabIndex==0)
		{	
			if(fn_querySpt())
			{
				tit_addSearchActionInfo("cs15:CS1501001A_S1");	

				tit_callService(
					""
					, ""
					, "ds_cond=ds_cond"
					, "ds_list1=ds_list1" 
					, ""
					, "fn_afterQuery"
					, true
					, true
					, true);		
			}
		}
		else if(tab.TabIndex==1)
		{	
			tit_addSearchActionInfo("cs15:CS1501001B_S1");	// ONHAND이거나 견적작성진행중인 경우 호기정보변경불가 로직추가 2015.03.25 YAR 
			tit_callService(
				""
				, ""
				, "ds_cond=ds_cond"
				, "ds_list2=ds_list2" 
				, ""
				, "fn_afterQuery"
				, true
				, true
				, true);						
		}
	}
}
/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() 
{
	if(ds_cond.GetColumn(0,"PJT")=="")
	{
		//alert("PJT NO를 입력하십시오.");
		gfn_showAlert("CW00001","Pjt No.");
		return;
	}
	if(ds_cond.GetColumn(0,"NAM")=="")
	{
		//alert("고객명을 입력하십시오.");
		gfn_showAlert("CW00001","고객명");
		return;
	}		
	if(ds_cond.GetColumn(0,"SPT")=="")
	{
		//alert("현장명을 입력하십시오.");
		gfn_showAlert("CW00001","현장명");
		return;
	}

	tit_clearActionInfo();

	tit_addSingleActionInfo("cs15:CS1501001A_U1");	

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , ""
        , ""
        , "fn_afterSave"
        , true
        , true
        , true);

}
