/********************************************************************************
* 기      능   : 확인 80
********************************************************************************/
function fn_confirm() 
{

	//fn_chkKunnr(); // 고객코드 확인하기

	// 거래선이 없는 경우 고객코드를 확인하지 않음. Full스캔 발생으로 인함. 2019.04.03 Han J.H
	if(ds_cond.GetColumn(0,"CST") != '') {
		fn_chkKunnr(); // 고객코드 확인하기
	}

	var kunnr = ds_kunnr.GetColumn(0, "KUNNR");
	var gno = ds_kunnr.GetColumn(0, "CS121_GNO");

	if(kunnr == "X") { 	
		var text = "사업자등록번호 : " + gno + "\n" 
		         + "고객정보가 존재하지 않습니다." + "\n" 
		         + "고객정보 등록 후 작업하십시오.";
		gfn_openMsgWin( text ,"W"); // 타이틀 warning massage
		return;
	}
//	trace(ds_kunnr.SaveXML());	return;	
	ds_list2Temp.ClearData();

	// 물류 분사 PSD 트랜젝션 제어 20170831 Start*****//	
	for(var i=0; i<ds_list2.RowCount(); i++) {
		if(substr(ds_list2.GetColumn(i,"HNO"),0,1) == "G"){
			gfn_showAlert("CW00246");
			return ;
		}
	}
	// 물류 분사 PSD 트랜젝션 제어 20170831 End*******//	

	for(var i=0; i<ds_list2.RowCount(); i++) 
	{
		if(ds_list2.GetColumn(i, "CHECK") == true && ds_list2.GetColumn(i,"STATUS") != "요청") 
		{
			if(user_grp=="20" || user_grp == "30" || user_grp == "40") 
			{
				var arr = [parseInt(i) + 1];
				gfn_showMsg("CW00146", arr);
				ds_list2.SetColumn(i, "CHECK", "0");
				ds_list2.updatecontrol = false;
				ds_list2.SetRowType(i, "Normal");
				ds_list2.updatecontrol = true;

				return;
			}
		}
	}

	if(ds_list2.GetUpdate()==false)
	{
		//alert("선택한 호기가 없습니다.");
		gfn_showAlert("CW00013");
	}	
	else
	{
		var newi=0;
		for(var i=0;i<ds_list2.RowCount();i++)
		{
			if(ds_list2.GetColumn(i,"CHECK")=="1")
			{
				ds_list2Temp.AddRow();
				ds_list2Temp.CopyRow(newi,ds_list2,i);
				newi=newi+1;
			}
		}	
		tit_clearActionInfo();
		//ZCST123 UPDATE
		tit_addSaveActionInfo(
			""
			, "cs15:CS1501001B_U2"
			, ""
			);		
		//ZCST122 INSERT
		tit_addSaveActionInfo(
			""
			, "cs15:CS1501001B_I1"
			, ""
			);		
		//ZCST121 UPDATE
		tit_addSaveActionInfo(
			""
			, "cs15:CS1501001B_U3"
			, ""
			);			
		//ZCST111 INSERT OR UPDATE
		tit_addSelectExActionInfo("cs15:CS1501001B_S2","cs15:CS1501001B_I2","cs15:CS1501001B_U4");

		tit_callService(
			""
			, ""
			, "ds_list2=ds_list2:U ds_list2Temp=ds_list2Temp"
			, ""
			, ""
			, "fn_query"
			, true
			, true
			, true);	
	}
}
