function fn_excel() 
{
	// Excel Export 
	if(tab.TabIndex == 0) {
		gfn_saveToExcel(tab.tab1.grd_list1, true);
	} else if(tab.TabIndex == 1) {
		gfn_saveToExcel(tab.tab2.grd_list2, true);
	}	
}
