/* 20080707 LJH 호기대수가 0 일때도 호기 추가가 가능하도록 수정*
		if(ds_list2.RowCount() == 0) {
			tab.TabIndex = 0;
			fn_query();
		}
*/
		gfn_showSearchMsg(tab.tab2.grd_list2);
	}
}
