function fn_querySpt() 
{			
	if(ds_cond.GetColumn(0,"UPN")=="")
	{
		//alert("통합Pjt No를 입력하세요.");	
		gfn_showAlert("CW00001","통합Pjt No.");
		return false;
	}
	else
	{	
		tit_clearActionInfo();		

		tit_addSearchActionInfo("cs15:CS1501001A_S2");	

		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_condData=ds_condData" 
			, ""
			, "fn_afterQuerySpt"
			, true
			, true
			, true);
	}	

	return true;
}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) 
{
	if ( ds_error.rowcount() > 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		gfn_showMsg("CI00003");
	}

	fn_query();
}
/********************************************************************************
* 기      능   : 현정명조회 Callback 
********************************************************************************/
function fn_afterQuerySpt()
{
	if(ds_condData.GetRowCount()>0)
	{
		ds_cond.DeleteAll();		
		ds_cond.copy(ds_condData);	
	}
	else
	{
		ds_cond.SetColumn(0,"SPT","");	
		ds_cond.SetColumn(0,"NAM","");	
	}
}
/********************************************************************************
* 기      능   : 취소 Callback 
********************************************************************************/
function fn_afterCancel() 
{
	fn_query();
}
/********************************************************************************
* 기      능   : 삭제 Callback 
********************************************************************************/
function ufn_afterDelete() 
{
	fn_query();
}
