/********************************************************************************
* 기      능   : 
********************************************************************************/
function ufn_showDetail(chk) 
{	
	ds_cond.SetColumn(0,"UPN",ds_list1.getColumn(ds_list1.currow,"UPN"));
	ds_cond.SetColumn(0,"CST",ds_list1.getColumn(ds_list1.currow,"CST"));
	ds_cond.SetColumn(0,"PJT",ds_list1.getColumn(ds_list1.currow,"PJT"));

	tab.TabIndex=1;
	if(chk == 'X')
	{	
		fn_query();
	}
}
/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function ufn_delete() 
{		
	ds_list3.ClearData();

	var t_cnt = 0;
	for(var i=0; i<ds_list2.RowCount(); i++) 
	{
		if(ds_list2.GetColumn(i, "CHECK") == true && ds_list2.GetColumn(i,"GBN") != "122") 
		{
			if(user_grp=="20" || user_grp == "30" || user_grp == "40") 
			{
				var arr = [parseInt(i) + 1];
				gfn_showMsg("CW00145", arr);
				return;
			}
		}

		if(ds_list2.GetColumn(i, "CHECK") == true) {
			t_cnt++;
		}
	}

	if(t_cnt == 0)
	{
		//alert("삭제하고자 하는 호기를 선택하십시오.");
		gfn_showAlert("CW00013");
	}
	else
	{
		var j = 0;
		for(var i=0; i<ds_list2.RowCount(); i++) 
		{
			if(ds_list2.GetColumn(i, "CHECK") == true) { //체크박스가 true 일 경우
				ds_list3.AddRow();
				ds_list3.SetColumn(j, "FLAG", "U");
				ds_list3.SetColumn(j, "PJT", ds_list2.GetColumn(i, "PJT"));
				ds_list3.SetColumn(j, "HNO", ds_list2.GetColumn(i, "HNO"));
				ds_list3.SetColumn(j, "UPN", ds_list2.GetColumn(i, "UPN"));
				ds_list3.SetColumn(j, "CST", ds_list2.GetColumn(i, "CST"));
				j++;
			}
		}
		//fn_queryChkDel();
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs15:CS1501001A_S4");	//ON-HAND 여부 조회(ZCST126)
//	tit_addSearchActionInfo("cs15:CS1501001A_S5");	//견적상태값 조회(ZCST301) 2015.02.26 YAR
	tit_callService(
		""
		, ""
		, "ds_list3=ds_list3"
		, "ds_chkDel=ds_list ds_chkDel2=ds_chkDel2"
		, ""
		, "fn_queryChkDel"
		, true);
}

