/********************************************************************************
* 기      능   : tab 이동시
********************************************************************************/
function tab_OnChanged(obj, nOldIndex, nNewIndex)
{
	if(ds_cond.GetColumn(0,"UPN")=="")
	{
		//alert("통합Pjt No를 입력하세요.");	
		gfn_showAlert("CW00001","통합Pjt No.");
		return false;
	}
	else if(user_grp=="20" || user_grp=="30" || user_grp=="40")
	{
		if(nNewIndex==0)
		{	
//			ufn_enableBtn("save",true);
			ufn_enableBtn("confirm",false);
			ufn_enableBtn("cancel",false);		
			fn_query();
		}
		else if(nNewIndex==1)
		{	
//			ufn_enableBtn("save",false);
			ufn_enableBtn("confirm",true);
			ufn_enableBtn("cancel",true);	
			ufn_showDetail("X");
		}		
	}
	else if(user_grp=="10") { // 2019.04.12 이준석 DL 요청. 협력사의 경우 호기정보 보이도록 처리.
		if(nNewIndex==0)
		{	
			fn_query();
		}
		else if(nNewIndex==1)
		{	
			ufn_showDetail("X");
		}			
	}
}
