function fn_OnLoadCompleted(obj) 
{
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	user_grp = gfn_getUserGroup();

	cb_gbn.Index = 0;
	ed_pjt.Enable = false;
	ed_upn.Enable = false;
	ed_cst.Enable = false;
	btn_upn.Enable = false;
	btn_pjt.Enable = false;
	ed_pjt.Text = "";
	ed_upn.Text = "";
	ed_cst.Text = "";
	//거래현황 조회 값 초기 셋팅	
	Tab00.tab6.cal_stada.value = substr(gfn_getCurrDate(),0,6) + "01";
	Tab00.tab6.cal_endda.value = gfn_getCurrDate();
	Tab00.tab6.cal_ddat.value = gfn_getCurrDate();		

	Tab00.TabIndex = 0;

	var codeArr = ["CURST"];
	var dsObjArr = [ ds_curst];
	var addTypeArr = [G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

}
