/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() 
{
	//조회조건 바꿀 경우 데이터 초기화 04.06.19
	ds_head.ClearData();
	ds_head2.ClearData();
	ds_list2.Cleardata();
	ds_list3.Cleardata();
	ds_list4.Cleardata();
	ds_list5.Cleardata();
	ds_list7.Cleardata();
	ds_list10.ClearData();

	//pjt 필수체크	
	if(gfn_isNull(cb_gbn, true, st_gbn.value))
		return;
	if(cb_gbn.value == "B") { //유상
		if(gfn_isNull(ed_upn, true, st_upn.value))
			return;	
		if(gfn_isNull(ed_cst, true, st_upn.value))
			return;	
	} else { // 무상 및 미발주
		if(gfn_isNull(ed_pjt, true, st_pjt.value))
			return;	
	}

	tit_clearActionInfo();
		tit_addSearchActionInfo("cs15:CS1502001A_S12"); //계약대수 
	if(cb_gbn.value == "B") { //유상		
		tit_addSearchActionInfo("cs15:CS1502001A_S1"); //고객정보	
		tit_addSearchActionInfo("cs15:CS1502001A_S2"); //계약정보

	} else {
		tit_addSearchActionInfo("cs15:CS1502001A_S7"); //원pjt 대수
	}
	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_head_decrypt=ds_head ds_head2=ds_head2 ds_list10=ds_list10"
        , ""
        , "fn_afterQuery"
        , true);

}
/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query1() 
{	
	tit_clearActionInfo();

	if(cb_gbn.value == "B" ) {		//유상
		tit_addSearchActionInfo("cs15:CS1502001A_S3"); //TAB1 : 설치정보
		tit_addSearchActionInfo("cs15:CS1502001A_S4"); //TAB2 : 사양정보
		tit_addSearchActionInfo("cs15:CS1502001A_S9"); //TAB3 : 보수정보	
		tit_addSearchActionInfo("cs15:CS1502001A_S6"); //TAB4 :유상계약정보
		tit_addSearchActionInfo("cs15:CS1502001A_S8"); //TAB6 : 자재청구이력
	}
	else {	
		tit_addSearchActionInfo("cs15:CS1502001A_S3"); //TAB1 : 설치정보
		tit_addSearchActionInfo("cs15:CS1502001A_S4"); //TAB2 : 사양정보
		tit_addSearchActionInfo("cs15:CS1502001A_S5"); //TAB3 : 보수정보
		tit_addSearchActionInfo("cs15:CS1502001A_S8"); //TAB6 : 자재청구이력		
	}

	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list2=ds_list2 ds_list3=ds_list3 ds_list4=ds_list4 ds_list5=ds_list5 ds_list7=ds_list7"
        , ""
        , "fn_afterQuery1"
        , true);
}
/********************************************************************************
* 기      능   : 조회(TBA5: 수금정보, 업체별 거래현황)
********************************************************************************/
function fn_query2() 
{
	ds_cond1.SetColumn(0, "STADA", Tab00.tab6.cal_stada.value); //청구기간 조회
	ds_cond1.SetColumn(0, "ENDDA", Tab00.tab6.cal_endda.value); //청구기간 조회
	ds_cond1.SetColumn(0, "DDAT", Tab00.tab6.cal_ddat.value); //기준일자 
	ds_cond1.SetColumn(0, "PROJ", ed_upn.value);
	ds_cond1.SetColumn(0, "GBN", "");

	tit_clearActionInfo();

	tit_callService(        
	      "CS3401001A_ACT"
        , "query"
        , "ds_cond=ds_cond1"
        , "ds_list6=ds_list ds_error=ds_error"
        , ""
        , "fn_afterQuery2"
        , false);

}
