/********************************************************************************
* 기      능   : 조회 Callback 
********************************************************************************/
function fn_afterQuery(errCode, errMsg) 
{	
   // 복호화 적용 201610 이하림
   // DEC_COL 복호화 대상
   // DEC_TYP  타입

	tit_callService(
         "COMM_ACT"
        , "getDsMultiDecrypt"
        , "ds_dec=ds_head_decrypt"
        , "ds_head=ds_list"
        , "DEC_COL=CS121_GNO,BANKB DEC_TYP=0,1"
        , "fn_afterDecrypt"
        , true);

}
function fn_afterDecrypt() {

	if (ds_head2.RowCount() > 0) {

		if(cb_gbn.value == "A" ) { //원PJT
			fn_query1();

		} else if(cb_gbn.value == "B" ) {
			fn_query1();
			fn_query2(); // 수금정보
		} 
	} else {
			gfn_showMsg("CW00010");	 //데이타가 없습니다.
	}

//	gfn_showMsg("CI00002",ds_head.RowCount()+"");
}
