/********************************************************************************
* 기      능   : print 40
********************************************************************************/
function fn_print() 
{
	ds_list9.ClearData();
	ds_list9.Reset();

	var tot_cnt = 0;
	var tot_amt = 0;
	var tot_su = 0;
	var mis_amt = 0;
	var tot_ncnt = 0;
	var tot_namt = 0;
	var tot_nsu = 0;
	var mis_namt = 0;
	var j = 0;
	var k = 0;

	ds_list9.AddRow();
	for(var i=0; i<ds_list6.RowCount(); i++) {
		if(substr(ds_list6.GetColumn(i, "POSID"),6,1) == 'N')
		{
			j++;
			tot_namt += ds_list6.GetColumn(i, "RKNETWR");
			tot_nsu += ds_list6.GetColumn(i, "HWBAS");
			mis_namt += ds_list6.GetColumn(i, "MISUA");
		} else {
			k++;
			tot_amt += ds_list6.GetColumn(i, "RKNETWR");
			tot_su += ds_list6.GetColumn(i, "HWBAS");
			mis_amt += ds_list6.GetColumn(i, "MISUA");
			}
	}
	ds_list9.SetColumn(0, "TOT_NCNT",   j); 
	ds_list9.SetColumn(0, "TOT_CNT",    k); 
	ds_list9.SetColumn(0, "TOT_AMT",    tot_amt);
	ds_list9.SetColumn(0, "TOT_NAMT",    tot_namt);
	ds_list9.SetColumn(0, "TOT_SU",     tot_su);
	ds_list9.SetColumn(0, "TOT_NSU",     tot_nsu);
	ds_list9.SetColumn(0, "MIS_AMT",     mis_amt);
	ds_list9.SetColumn(0, "MIS_NAMT",     mis_namt);
	ds_list9.SetColumn(0, "MIS_TOT",     mis_amt+mis_namt);

	ds_list5.SetColumn(0,  "UGS", ds_head2.GetColumn(0, "CS126_USD") ); // 온핸드정보 이하림
	ds_list5.SetColumn(0,  "UHJ", ds_head2.GetColumn(0, "CS126_UMR") ); // 온핸드정보 이하림

	Dialog("CS15::CS1502001C.xml", "");		//고객정보카드 출력_유수진
}
