function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) 
	{
		switch (Tab00.TabIndex) 
		{
			case 0 :gfn_selectGridCol("Tab00.tab2.grd_list2",true);
					break;
			case 1 :gfn_selectGridCol("Tab00.tab3.grd_list3",true);
					break;
			case 2 :gfn_selectGridCol("Tab00.tab4.grd_list4",true);
					break;	
			case 3 :gfn_selectGridCol("Tab00.tab5.grd_list5",true);
					break;		
			case 4 :gfn_selectGridCol("Tab00.tab6.grd_list6",true);
					break;		
			case 5 :gfn_selectGridCol("Tab00.tab7.grd_list7",true);
					break;			
		}
	} 
	else if ( nCell > 0 ) 
	{
		gfn_sortGrid(obj, nCell);
	}	
}
/********************************************************************************
* 기      능   : 
********************************************************************************/
function Tab00_tab3_common_OnClick(obj,strValue)
{
	filterList();
}
function filterList()
{
	var chk_a = "";
	var chk_b = "";
	var chk_c = "";
	var chk_d = "";
	var chk_e = "";
	var chk_f = "";
	var chk_s = "";

	var val_a = Tab00.tab3.chk_a.Value;
    var val_b = Tab00.tab3.chk_b.Value;
    var val_c = Tab00.tab3.chk_c.Value;
    var val_d = Tab00.tab3.chk_d.Value;
    var val_e = Tab00.tab3.chk_e.Value;
    var val_f = Tab00.tab3.chk_f.Value;
    var val_s = Tab00.tab3.chk_s.Value;

	if(val_a == 'X') chk_a = "EL_A";
	if(val_b == 'X') chk_b = "EL_B";
	if(val_c == 'X') chk_c = "EL_C";
	if(val_d == 'X') chk_d = "EL_D";
	if(val_e == 'X') chk_e = "EL_E";
	if(val_f == 'X') chk_f = "|| (left(CODE,4)!='EL_A' && left(CODE,4)!='EL_B' && left(CODE,4)!='EL_C' && left(CODE,4)!='EL_D' && left(CODE,4)!='EL_E')";
	if(val_s == 'X') chk_s = "|| (left(CODE,6)=='EL_CJM' || left(CODE,7)=='EL_ECJJ' || left(CODE,8)=='EL_EHTRH' || left(CODE,7)=='EL_EHTH' || left(CODE,6)=='EL_ETM' || left(CODE,7)=='EL_ERPR' || left(CODE,8)=='EL_ECWTP' || left(CODE,7)=='EL_ERBQ' || left(CODE,7)=='EL_ESPB' || left(CODE,6)=='ES_BMP' || left(CODE,7)=='ES_BBRU' || left(CODE,8)=='ES_AHIGH' || left(CODE,6)=='MW_BMP' || left(CODE,7)=='MW_BBRU' || left(CODE,8)=='MW_AHIGH')";

	if(val_a+val_b+val_c+val_d+val_e+val_f+val_s == "0000000")
	{
		ds_list3.Filter("1==1");
	}	
	else 
		ds_list3.Filter("left(CODE,4)=="+quote(chk_a)+" || left(CODE,4)=="+quote(chk_b)+" || left(CODE,4)=="+quote(chk_c)+" || left(CODE,4)=="+quote(chk_d)+" || left(CODE,4)=="+quote(chk_e)+chk_f+chk_s);
}

function ufn_cellDisplay(currow)
{
	if(currow == ds_list9.RowCount()) return "number";
	else return "text";
}
function ufn_cellAlign(currow)
{
	if(currow == ds_list9.RowCount()) return "right";
	else return "center";
}
