/********************************************************************************
* 기      능   : 검사 성적서 팝업
********************************************************************************/
function Tab00_tab2_grd_list2_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	// 성적서 유형 : 검사상태 클릭시
	if( nCell==11 ) //&& ( ds_list2.GetColumn(nRow, "DSCODE") == '합격' || ds_list2.GetColumn(nRow, "DSCODE") == '불합격' ) )
	{
		var gbn = substr(ds_list2.GetColumn(nRow, "POSID_1"), 6, 1);
		var pjt = substr(ds_list2.GetColumn(nRow, "POSID_1"), 0, 6);
		var hno = ds_list2.GetColumn(nRow, "POSID_1");

		fn_goNextForm(gbn,pjt,hno);
	}		
}

function fn_goNextForm(gbn,pjt,hno){
	var formID="";

	switch (gbn) {

		case 'L':
			formID = "PS0101001L";
			break;			

		case 'S':
			formID = "PS0101001S";
			break;

		case 'W':
			formID = "PS0101001S";
			break;	

	}

	var arg = "PRE_PJT="+quote(pjt);
		arg += " PRE_HNO="+quote(hno);

	//SELFLG = "N";
	gfn_openPopup(formID, true, arg);
	//if(SELFLG == "Y") fn_query();
}

/********************************************************************************
* 기      능   : 조회 구분값 (A:무상/미빌주 , B:유상)
********************************************************************************/
function cb_gbn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(cb_gbn.Value == "A") {
		ed_pjt.Enable = true;
		ed_upn.Enable = false;
		ed_cst.Enable = false;
		btn_pjt.Enable = true;
		btn_upn.Enable = false;
		ed_upn.Text = "";
		ed_cst.Text = "";
	} else if(cb_gbn.Value == "B") {
		ed_pjt.Enable = false;
		ed_upn.Enable = true;
		ed_cst.Enable = true;
		btn_pjt.Enable = false;
		btn_upn.Enable = true;
		ed_pjt.Text = "";
	} else {
		ed_pjt.Enable = false;
		ed_upn.Enable = false;
		ed_cst.Enable = false;
		btn_pjt.Enable = false;
		btn_upn.Enable = false;
		ed_pjt.Text = "";
		ed_upn.Text = "";
		ed_cst.Text = "";
	}
}

