var btn_del_auth = false; // 통합거래처 삭제 버튼 권한
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	gfn_initForm();

	var codeArr = ["CS125","CS126","CS127","CS128","CS139","ZTERM","CURST"];	
	var dsObjArr = [ds_balhangGubun,ds_balsongGubun,ds_calculator,ds_buildGubun,ds_cstGubun,ds_zterm, ds_curst];
	var addTypeArr = [G_CODE_NONE,G_CODE_NONE,G_CODE_NONE,G_CODE_SELECT,G_CODE_NONE,G_CODE_SELECT,G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);	

	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS1601001A");
	ds_rcond.SetColumn(0, "GBN", "01");	
	btn_del_auth = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();

}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_buildGubun.Index = 0;
	cb_balhangGubun.Index = 0;
	cb_balsongGubun.Index = 0;
	cb_jugi.Index = 0;
	cb_cstGubun.Index = 0;
	cb_sla.Index = 0;
	rd_iche.Index = 1;
	rd_bosu.Index = 1;
	rd_suri.Index = 1;
	rd_rsn.Index = 1;
	cb_zterm.Index = 0;

	ds_zterm.Filter("CODE != '0002')");	


	ufn_objUse();
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query2() {
	if(ufn_checkSearchCondition()) {
		ds_data.Clear();
		ds_logList.Clear();
		tit_clearActionInfo();
		tit_addSearchActionInfo("cs16:CS1601001A_S2"); // zcst121  
		tit_addSearchActionInfo("cs16:CS1601001A_S8"); // zcst124  변경 이력 조회
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_data_decrypt=ds_data ds_logList=ds_logList"
			, ""
			, "fn_afterQuery"
			, true);
	}
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {

   // 복호화 적용 201610 이하림
   // DEC_COL 복호화 대상
   // DEC_TYP  타입

	tit_callService(
         "COMM_ACT"
        , "getDsMultiDecrypt"
        , "ds_dec=ds_data_decrypt"
        , "ds_data=ds_list"
        , "DEC_COL=GNO,BANKB DEC_TYP=0,1"
        , "fn_afterDecrypt"
        , true);

}
function fn_afterDecrypt() {
	// 메시지 처리 
//	gfn_showSearchMsg(grd_list);

	chk_email.value = true;
//	ds_zterm.Filter("CODE <= '0110')");

}
