function ufn_openUpn() {
	var arg = "upn="+quote(ed_upn.Text)+" cnt="+quote("0");
	gfn_openPopup("CS9601001A",true,arg);
}

/********************************************************************************
* 기      능   : 우편번호 검색 
********************************************************************************/
var strZipcodeObj;
function ufn_openZipcode(Obj) {		

	var arg = "strObj="+quote(Obj.id);
	strZipcodeObj=Obj.id;
	AxMSIE0.Navigate2(G_SERVER_URL + "addressSearchLink.jsp");
	//gfn_openPopup("CS9601003A",true,arg);	
}

var chk=0;

function AxMSIE0_TitleChange(obj,Text)
{
	chk++;
	trace("chk="+chk);
	trace("Text="+Text);
	if(Text!="address link"){
		if(chk==2){	
					trace("xxxx");

			ufn_setAddr();
			chk=1;

		}
	}else{
		chk=1;
	}
}

function ufn_setAddr() {

	var objDocument = AxMSIE0.Document;

	var roadAddrPart1 = objDocument.getElementById('roadAddrPart1').value;
	var roadAddrPart2 = objDocument.getElementById('roadAddrPart2').value;

	var roadAddr = roadAddrPart1 + ' ' + roadAddrPart2;	
	var jibunAddr = objDocument.getElementById('jibunAddr').value;

	var addrDetail = objDocument.getElementById('addrDetail').value;
	// 우편번호 6자리 >> 5자리 변경 201508
	var zipNo = objDocument.getElementById('zipNo').value; 

	if(strZipcodeObj=="btn_zipcode"){
		if(IsValid(Object("med_zipcode"))!=false){
//			med_zipcode.value = zipNo;				
		}	
		if(IsValid(Object("ed_addr"))!=false){			
			ed_addr.value = roadAddr;	
		}			
		if(IsValid(Object("ed_addrDetail"))!=false){			
			ed_addrDeail.value = addrDetail;	
		}			
	}
	else if(strZipcodeObj=="btn_zipcode0" || strZipcodeObj=="btn_zipcode0_road"){
		if(IsValid(Object("med_zipcode0"))!=false){
//			med_zipcode0.value = zipNo;				
		}	
		if(IsValid(Object("ed_addr0"))!=false){			
			ed_addr0.value = jibunAddr;	
		}
		if(IsValid(Object("ed_addrDetail0"))!=false){			
			ed_addrDetail0.value = addrDetail;	
		}

		//도로명주소
		if(IsValid(Object("med_zipcode0_road"))!=false){
//			med_zipcode0_road.value = zipNo;				
		}	
		if(IsValid(Object("ed_addr0_road"))!=false){			
			ed_addr0_road.value = roadAddr;	
		}									
		if(IsValid(Object("ed_addrDetail0_road"))!=false){			
			ed_addrDetail0_road.value = addrDetail;	
		}
	}
	// 지로수신처 주소 변경 시 
	else if(strZipcodeObj=="btn_zipcode1" || strZipcodeObj=="btn_zipcode1_road"){
		if(IsValid(Object("med_zipcode1"))!=false){
//			med_zipcode1.value = zipNo;			
			ed_nziB.value = zipNo;					
		}	
		if(IsValid(Object("ed_addr1"))!=false){			
			ed_addr1.value = jibunAddr;	
		}
		if(IsValid(Object("ed_addrDetail1"))!=false){			
			ed_addrDetail1.value = addrDetail;	
		}

		//도로명주소
		if(IsValid(Object("med_zipcode1_road"))!=false){
//			med_zipcode1_road.value = zipNo;			
			ed_nziB.value = zipNo;				
		}	
		if(IsValid(Object("ed_addr1_road"))!=false){			
			ed_addr1_road.value = roadAddr;	
		}
		if(IsValid(Object("ed_addrDetail1_road"))!=false){			
			ed_addrDetail1_road.value = addrDetail;	
		}												
	}
	else if(strZipcodeObj=="btn_zipcode2"){
		if(IsValid(Object("med_zipcode2"))!=false){
//			med_zipcode2.value = zipNo;				
		}	
		if(IsValid(Object("ed_addr2"))!=false){			
			ed_addr2.value = roadAddr;	
		}		
		if(IsValid(Object("ed_addrDetail3"))!=false){			
			ed_addrDetail2.value = addrDetail;	
		}			
	}
	else if(strZipcodeObj=="btn_zipcode3"){
		if(IsValid(Object("med_zipcode3"))!=false){
//			med_zipcode3.value = zipNo;	
		}	
		if(IsValid(Object("ed_addr3"))!=false){			
			ed_addr3.value = roadAddr;	
		}
		if(IsValid(Object("ed_addrDetail3"))!=false){			
			ed_addrDetail3.value = addrDetail;	
		}					
	}		
}
