/********************************************************************************
* 기      능   : ds_cond 의 value 변경 시 거래선 정보 조회
********************************************************************************/
function ufn_setCstCombo(obj,nRow,strColumnID,varOldValue,varNewValue) {
	if(strColumnID == "UPN") {
		ds_cst.DeleteAll();
		tit_clearActionInfo();
		tit_addSearchActionInfo("cs16:CS1601001A_S1");

		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_cst=ds_cst"
			, ""
			, "ufn_afterSetCstCombo"
			, true);
	} 
}

/********************************************************************************
* 기      능   : 거래선 콤보 value 변경 시 거래선 정보 조회
********************************************************************************/
function cb_cst_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(cb_cst.value == '') {
			fn_query2();
		} else {
			fn_query();
		}
}

function ufn_afterSetCstCombo() {

	if(length(trim(ed_cst.Text)) > 0) {
		cb_cst.Value = ed_cst.Text;
		fn_query();
	} else {
		fn_query2();
	}
	ed_cst.Text = "";

}

function ufn_checkGno() {	
	if(ds_data.GetColumn(0,"GNO") == ds_data.GetOrgColumn(0,"GNO")) {
		return;
	} else {
		// tit_clearActionInfo();
		// tit_addSearchActionInfo("cs16:CS1601001A_S3");	
		// tit_callService(
			// ""
			// , ""
			// , "ds_data=ds_data"
			// , "ds_gnoResult=ds_gnoResult"
			// , ""
			// , "ufn_afterCheckGno"
			// , true);

	   // 암호화 적용 201610 이하림
	   // ENC_COL 암호화 대상
	   // ENC_TYP  타입

		tit_callService(
			 "COMM_ACT"
			, "getDsEncrypt"
			, "ds_enc=ds_data"
			, "ds_data_decrypt=ds_list"
			, "ENC_COL=GNO ENC_TYP=0"
			, "fn_AfterEncrypt"
			, true);	

		}
}

function fn_AfterEncrypt() {

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs16:CS1601001A_S3");	
	tit_callService(
		""
		, ""
		, "ds_data=ds_data_decrypt"
		, "ds_gnoResult=ds_gnoResult"
		, ""
		, "ufn_afterCheckGno"
		, true);
}
function ufn_afterCheckGno() {
	if(ds_gnoResult.GetRowCount() == 0) {
		//alert("해당 사업자등록번호는 존재하지 않습니다.");
		gfn_showAlert("CW00105");
		ds_data.SetColumn(0,"GNO","");
		ds_data.SetColumn(0,"GNAME","");
		ds_data.SetColumn(0,"ADDR","");
		ds_data.SetColumn(0,"PNAME","");
		ds_data.SetColumn(0,"EGNAME","");
		ds_data.SetColumn(0,"ZIE","");
		ds_data.SetColumn(0,"P1E","");
		ds_data.SetColumn(0,"P2E","");
		ds_data.SetColumn(0,"EPNAME","");


	} else {
		ds_data.SetColumn(0,"GNAME",ds_gnoResult.GetColumn(0,"NAME"));
		ds_data.SetColumn(0,"ADDR",ds_gnoResult.GetColumn(0,"ADDR"));
		ds_data.SetColumn(0,"PNAME",ds_gnoResult.GetColumn(0,"PNAME"));

		ds_data.SetColumn(0,"EGNAME",ds_gnoResult.GetColumn(0,"NAME"));
		ds_data.SetColumn(0,"ZIE",ds_gnoResult.GetColumn(0,"PSTLZ"));
		ds_data.SetColumn(0,"P1E",ds_gnoResult.GetColumn(0,"ADDR"));
		ds_data.SetColumn(0,"EPNAME",ds_gnoResult.GetColumn(0,"PNAME"));

		ds_data.SetColumn(0,"KUNNR",ds_gnoResult.GetColumn(0,"KUNNR"));
		ds_data.SetColumn(0,"GNO_ENCRYPT",ds_gnoResult.GetColumn(0,"GNO_ENCRYPT")); // 암호화 된 주민번호
	}
}

function ufn_checkStr() {
	if(ds_data.GetColumn(0,"SJU") != "N" && ds_data.GetColumn(0,"STR") < gfn_getCurrDate()) {		
		gfn_showAlert("CW00007","적용시작일자","현재일자");
		ds_data.SetColumn(0,"STR","");
	}
}

/********************************************************************************
* 기      능   : 수정 버튼 처리
********************************************************************************/
function ufn_modify() {
	var user_grp = gfn_getUserGroup();

	//var posid = ed_upn.value + cb_cst.Value;
	//if(gfn_psdCheck(posid))	{
	//	gfn_showAlert("CW00245");		
	//	return;
	//}
	if(substr(ed_upn.value,0,1) == "G"){
		gfn_showAlert("CW00246");		
		return;
	}
	fn_repChar(); // 개행문자 제거
	fn_mdfLog(); // 변경이력 set dataset

	if(user_grp == "10") { // 협력사일 경우
		if(Length(Trim(ed_upn.Text)) == 0) {
			gfn_showAlert("CW00001","통합Pjt.No");		
			return false;
		}

		if(ufn_checkCondition() == false) {	
			return false;
		}

		tit_clearActionInfo();
		tit_addSingleActionInfo("cs16:CS1601001A_U4");
		tit_callService(
				""
				, ""
				, "ds_data=ds_data"
				, ""
				, ""
				, ""
				, true							
				, false							
				, true); 	

		fn_saveLog();// 변경이력 저장 ZCST124  20170801

	} else { // 그외
		if(ufn_checkUpdateCondition()) {			
			ds_data.SetColumn(0, "GSHCDP", ds_data.GetColumn(0,"GSH") + " " + ds_data.GetColumn(0,"CDP"));
			var t_rdt = "";
			t_rdt = ds_data.GetColumn(0, "RDT");

			if(length(t_rdt) == 1) {
				t_rdt = "0" + t_rdt;
			} else {
				t_rdt = t_rdt;
			}

			ds_data.SetColumn(0, "RDT", t_rdt);

			tit_clearActionInfo();
			//ufn_modifyUpdate122();
			tit_addSingleActionInfo("cs16:CS1601001A_U1"); // ZCST121 UPDATE 
			// 수신처주소(ZFIT1031) modifyUpdate				
			var fncstr = "  AND KUNNR = SAPHEE.GET_VEND_CODE('"
					   + G_MANDT
					   + "', '"
					   + ds_data.GetColumn(0,"GNO_ENCRYPT")
					   + "')";

			//trace("tit_addSingleActionInfo('cs16:CS1601001A_U3')");

			tit_addSingleActionInfo("cs16:CS1601001A_U3");
			tit_addSingleActionInfo("cs16:CS1601001A_U5");    // ZCST126 영세율 여부 변경 UPDATE 
			//ufn_modifyInsert152();  
			//tit_addSingleActionInfo("cs16:CS1601001A_I1");

			tit_callService(
				""
				, ""
				, "ds_data=ds_data"
				, ""
				, "FNCSTR="+quote(fncstr)
				, ""
				, true							
				, false							
				, true); 	

			fn_saveLog();// 변경이력 저장 ZCST124 
		}
	}
	//fn_query2();//
}

/********************************************************************************
* 기      능   : 입력 버튼 처리
********************************************************************************/
function ufn_checkInsert() {
	ds_data.SetColumn(0,"UPN",ds_cond.GetColumn(0,"UPN"));

	//var posid = ed_upn.value + cb_cst.Value;
	//if(gfn_psdCheck(posid))	{
	//	gfn_showAlert("CW00245");		
	//	return;
	//}
	if(substr(ed_upn.value,0,1) == "G"){
		gfn_showAlert("CW00246");		
		return;
	}
	fn_repChar();

	if(ufn_checkInsertCondition()) {	
		tit_clearActionInfo();
		tit_addSearchActionInfo("cs16:CS1601001A_S6");

		tit_callService(
			""
			, ""
			, "ds_data=ds_data"
			, "ds_gnoCnt=ds_gnoCnt"
			, ""
			, "ufn_setCst"
			, true);
	}
}

function ufn_setCst() {
	if(ds_gnoCnt.GetColumn(0,"CNT") > 0) {
		//alert("본 통합프로젝트에 이미 존재하는 사업자번호 입니다.");
		gfn_showAlert("CW00106");
	} else {
		tit_addSearchActionInfo("cs16:CS1601001A_S4");				
		tit_callService(
			""
			, ""
			, "ds_data=ds_data"
			, "ds_cstResult=ds_cstResult"
			, ""
			, "ufn_insert"
			, true);
	}	
}

function ufn_insert() {	
	ds_data.SetColumn(0,"NEWCST",ds_cstResult.GetColumn(0,"CST"));
	ds_data.SetColumn(0,"CST",ds_cstResult.GetColumn(0,"CST"));

	ds_cond.FireEvent=false;
	ds_cond.SetColumn(0,"NEWCST",ds_cstResult.GetColumn(0,"CST"));
	ds_cond.FireEvent=true;

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs16:CS1601001A_S5");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_122Result=ds_122Result"
        , ""
        , ""
        , true
        , true
        , true);

	fn_repChar();

	ds_122Result.updatecontrol = false;

	for(var i=0; i<ds_122Result.GetRowCount(); i++) {
		ds_122Result.SetRowType(i,"Update");
	}

	ds_122Result.updatecontrol = true;  

	tit_clearActionInfo();
	//ufn_insertInsert121();	
	tit_addSingleActionInfo("cs16:CS1601001A_I2");
	if(cb_cstGubun == "1" || cb_cstGubun == "2") {
		//ufn_insertInsert122();	
		//tit_addSingleActionInfo("cs16:CS1601001A_I3");
		//ufn_insertInsert152();	
		tit_addSingleActionInfo("cs16:CS1601001A_I1");
		//ufn_insertUpdate111();
	}
/* 2009.03.10 LJH 통합거래처 신규 생성시 ZCST111의 호기에 UPN, CST update 하는 부분 제외 - 의미 없는 부분으로 판단
	tit_addSaveActionInfo(
		""
		, "cs16:CS1601001A_U2"
		, ""
		);		
*/
	tit_callService(
        ""
        , ""
        , "ds_data=ds_data:A ds_cond=ds_cond:A ds_122Result=ds_122Result:A"
        , ""
        , ""
        , "ufn_afterInsert"
        , true);     

}

function ufn_afterInsert() {
	ufn_setCstCombo("","","UPN");	
}
/********************************************************************************
* 기      능   : 삭제 가능여부 확인
********************************************************************************/
function ufn_checkDelete(obj)
{
	ds_gnoCnt2.ClearData();
	ds_gnoCnt3.ClearData();
	ds_gnoCnt2.AddRow();
	ds_gnoCnt3.AddRow();

	//var posid = ed_upn.value + cb_cst.Value;
	//if(gfn_psdCheck(posid))	{
	//	gfn_showAlert("CW00245");		
	//	return;
	//}
	if(substr(ed_upn.value,0,1) == "G"){
		gfn_showAlert("CW00246");		
		return;
	}

	ds_data.SetColumn(0,"UPN",ds_cond.GetColumn(0,"UPN"));
	ds_data.SetColumn(0,"CST",ds_cond.GetColumn(0,"CST"));

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs16:CS1601001A_S9");
	tit_addSearchActionInfo("cs16:CS1601001A_S10");
	tit_addSearchActionInfo("cs16:CS1601001A_S11");
	tit_callService(
		""
		, ""
		, "ds_data=ds_data"
		, "ds_gnoCnt2=ds_gnoCnt2 ds_gnoCnt3=ds_gnoCnt3 ds_gnoCnt4=ds_gnoCnt4"
		, ""
		, "ufn_Delete"
		, true);
}

/********************************************************************************
* 기      능   : 삭제 버튼 처리
********************************************************************************/
function ufn_Delete(obj)
{
	if(length(ds_data.GetColumn(0, "CST")) == 0)
	{
		gfn_showAlert("CW10025", "거래선 정보가 없는 경우 삭제가 불가합니다.");
		return;
	}
	if(ds_gnoCnt2.GetColumn(0, "CNT") > 0)
	{
		gfn_showAlert("CW10025", "유상계약이 존재하여 삭제가 불가합니다.");
    	return;
    }
    else{

		// ---------보수자재 견적에 거래선 사용여부 체크 추가 (최선경씨 요청 2018.09/14) ---------
		if(ds_gnoCnt4.GetColumn(0, "CNT") > 0)
		{	
			gfn_showAlert("CW10025", "자재청구 견적내역이 존재하여 삭제가 불가합니다.");
			return;
    	}
        // ---------------------------------------------------------------------------------------


		if(ds_gnoCnt3.GetColumn(0, "CNT") > 0)
		{	
			gfn_showAlert("CW10025", "자재청구내역이 존재하여 삭제가 불가합니다.");
			return;
    	}
    }

    tit_clearActionInfo();
	tit_addMultiActionInfo("cs16:CS1601001A_D1");
	tit_addMultiActionInfo("cs16:CS1601001A_D2");
	tit_addMultiActionInfo("cs16:CS1601001A_D3");
	tit_callService(
	""
	, ""
	, "ds_data=ds_data"
	, ""
	, ""
	, ""
	, true);

	gfn_showAlert("CI30001", "삭제처리가 완료되었습니다.");
	fn_query();
}

/********************************************************************************
* 기      능   : 조회 버튼 처리
********************************************************************************/
function fn_query() {
	if(length(trim(cb_cst.value)) == 0) {
		fn_query2();
	} else {
		ds_gcond.SetColumn(0,"UPN",ed_upn.value);
		ds_gcond.SetColumn(0,"CST",cb_cst.value);

		tit_clearActionInfo();
		tit_addSearchActionInfo("cs16:CS1601001A_S7");

		tit_callService(
			""
			, ""
			, "ds_gcond=ds_gcond"
			, "ds_gno=ds_gno"
			, ""
			, "ufn_afterGno"
			, true);
	}
}

function ufn_afterGno() {
	if(ds_gno.GetColumn(0,"KUNNR") > '') {
		fn_query2();
	} else {
		gfn_showAlert("CW00148");
		fn_query2();
	}
}

function ufn_checkSearchCondition() {	
	if(Length(Trim(ds_cond.GetColumn(0,"UPN"))) == 0) {
		gfn_showAlert("CW00001","통합Pjt.No");	
		return false;
	}

	return true;
}

function ufn_checkInsertCondition() {
	if(Length(Trim(ds_data.GetColumn(0,"CSB"))) == 0) {
		gfn_showAlert("CW00001","거래처구분");
		return false;
	}

	if(length(Trim(ds_data.GetColumn(0,"TMK"))) > 30) {
		var arr = [st_tmk.Value, "30글자"];
		gfn_showAlert("CW00133", arr);
		return false;
	}

	if(length(Trim(ds_data.GetColumn(0,"JNO"))) > 0 && length(Trim(ds_data.GetColumn(0,"JNO"))) < 4) {
		gfn_showAlert("종사업장번호는 4자리입니다.\n다시 한번 확인바랍니다.");
		return false;
	}
//	alert("TGNO ==> [" + ds_data.GetColumn(0,"TGNO") + "]");
//	alert("GNO ==> [" + ds_data.GetColumn(0,"GNO") + "]");
//	alert("CST ==> [" + substr(cb_cst.value,0,1) + "]");
//	alert("GBN ==> [" + cb_buildGubun.value + "]");
	if(ds_data.GetColumn(0,"TGNO") == ds_data.GetColumn(0,"GNO")) {
		if(cb_cst.value > '') {
			if(substr(cb_cst.value,0,1) <> 'C' && cb_buildGubun.value <> 'C') {
				gfn_showAlert("CW00106");
				return false;
			}
		}
	}

	if(ufn_checkCondition() == false) {	
		return false;
	}

	return true;
}

function ufn_checkUpdateCondition() {
	// 20081023 LJH 기존 사업자 번호 변경후 수정은 무조건 안됨, 사업자 번호 변경후에는 등록 처리(김재순 과장 요청)
	//if(Trim(ds_data.GetColumn(0,"TGNO")) <> Trim(ds_data.GetColumn(0,"GNO"))) {
	if(Trim(ds_data.GetOrgColumn(0,"GNO")) <> Trim(ds_data.GetColumn(0,"GNO"))) {
		gfn_showAlert("CW00107","사업자등록번호");		
		return false;
	}

	var cstGubun = ds_data.GetOrgColumn(0, "CSB") ;
	if(cb_cstGubun.Value != cstGubun) {
		//gfn_showAlert("거래처구분을 수정하실수 없습니다.");	
		gfn_showAlert("CW00107","거래처구분");	
		return false;
	}

	if(length(Trim(ds_data.GetColumn(0,"TMK"))) > 30) {
		var arr = [st_tmk.Value, "30글자"];
		gfn_showAlert("CW00133", arr);
		return false;
	}

	if(length(Trim(ds_data.GetColumn(0,"JNO"))) > 0 && length(Trim(ds_data.GetColumn(0,"JNO"))) < 4) {
		gfn_showAlert("종사업장번호는 4자리입니다.\n다시 한번 확인바랍니다.");
		return false;
	}
/*
	if(!(cb_cstGubun.Value=="1" || cb_cstGubun.Value=="2")){
		//gfn_showAlert("거래처구분이 '전체' 혹은 '정기'인 경우에만 수정하실수 있습니다.");	
		gfn_showAlert("CW00108");
		return false;
	}	
*/
	if(ufn_checkCondition() == false) {	
		return false;
	}

	return true;	
}

function ufn_checkCondition() {
	if(Length(Trim(ds_data.GetColumn(0,"UPN"))) == 0) {
		gfn_showAlert("CW00001","통합Pjt.No");		
		return false;
	}
	if(Length(Trim(ds_data.GetColumn(0,"GNO"))) == 0) {		
		gfn_showAlert("CW00001","사업자등록번호");		
		return false;
	}
	if(Length(Trim(ds_data.GetColumn(0,"GNAME"))) == 0) {
		gfn_showAlert("CW00001","상호명");			
		return false;
	}	
	if(Length(Trim(ds_data.GetColumn(0,"ADDR"))) == 0) {
		gfn_showAlert("CW00001","사업장주소");
		return false;
	}	
	if(Length(Trim(ds_data.GetColumn(0,"ABG"))) == 0) {
		gfn_showAlert("CW00001","빌딩구분");
		return false;
	}	
	if(Length(Trim(ds_data.GetColumn(0,"NAM"))) == 0) {
		gfn_showAlert("CW00001","고객명");
		return false;
	}
	if(Length(Trim(ds_data.GetColumn(0,"ZIE"))) == 0) {
		gfn_showAlert("CW00001","사업장 우편번호");
		return false;
	}
	if(Length(Trim(ds_data.GetColumn(0,"P1E"))) == 0) {
		gfn_showAlert("CW00001","사업장 주소");
		return false;
	}
	// 사업장주소-지번주소 입력 시 자릿수 초과 에러로 유효성 조건 추가. 2020.12.09 Han J.H
	if(Lengthb(Trim(ds_data.GetColumn(0,"P1E"))) > 150) {
		gfn_showAlert("사업장주소의 주소1 입력내용이 너무 깁니다.\n150 bytes 이하로 입력하십시요.");
		return false;
	}	
	if(Length(Trim(ds_data.GetColumn(0,"EGNAME"))) == 0) {
		gfn_showAlert("CW00001","사업장 상호");
		return false;
	}
	if(Length(Trim(ds_data.GetColumn(0,"EPNAME"))) == 0) {
		gfn_showAlert("CW00001","사업장 대표자명");
		return false;
	}
	// if(Length(Trim(ds_data.GetColumn(0,"ZIB"))) == 0) {
		// gfn_showAlert("CW00001","세금계산서 우편번호");
		// return false;
	// }	
	if(Length(Trim(ds_data.GetColumn(0,"P1B"))) == 0 ) {
		gfn_showAlert("CW00001","지로수신처 주소(지번)");
		return false;
	}
	if(Length(Trim(ds_data.GetColumn(0,"GSH"))) == 0) {
		gfn_showAlert("CW00001","상호");
		return false;
	}	
	if(Length(Trim(ds_data.GetColumn(0,"CDP"))) == 0) {
		gfn_showAlert("CW00001","담당부서");
		return false;
	}	

	if(Length(Trim(ds_data.GetColumn(0,"TEA"))) == 0) {
		gfn_showAlert("CW00001","담당부서 전화번호");
		return false;
	}	
	//====================담당부서전화번호 검증로직 추가============================
	else if(gfn_checkPhoneNumber(ds_data.GetColumn(0,"TEA"))) {
		gfn_showAlert("올바른 담당부서 전화번호를 입력해주십시오.");
		return false;
	}		
	//==============================================================================	
	if(Length(Trim(ds_data.GetColumn(0,"ZTERM"))) == 0) {
		gfn_showAlert("CW00001","수금기준");
		return false;
	}
	if(Length(Trim(ds_data.GetColumn(0,"SJU"))) == 0) {
		gfn_showAlert("CW00001","자동이체여부");
		return false;
	}
	if(ds_data.GetColumn(0,"ZTERM") >= "0025"){
		if(ds_data.GetColumn(0,"SJU") == "N" ) {
			gfn_showAlert("수금기준 항목과 자동이체여부를 확인하십시오.");
			return false;
		}
	}
	if(Length(Trim(ds_data.GetColumn(0,"CAR"))) == 0) {
		gfn_showAlert("CW00001","카드과세");
		return false;
	}
/*
	if(ds_data.GetColumn(0,"SJU") == "Y" && Length(Trim(ds_data.GetColumn(0,"STR"))) == 0) {
		gfn_showAlert("CW00004","자동이체여부","적용시작일자");
		return false;
	}	
	if(Trim(ds_data.GetColumn(0,"STR")) < gfn_getCurrDate()) {
		gfn_showAlert("CW00007","적용시작일자","현재일자");
		return false;
	}
*/
	if(Length(Trim(ds_data.GetColumn(0,"SLA"))) == 0) {
		gfn_showAlert("CW00001","하도급금지대상");
		return false;
	}
	if(Length(Trim(ds_data.GetColumn(0,"GBN"))) == 0) {
		gfn_showAlert("CW00001","계산서수신구분");
		return false;
	}
	if(Length(Trim(ds_data.GetColumn(0,"BGB"))) == 0) {
		gfn_showAlert("CW00001","발송구분");
		return false;
	}		
	if(Length(Trim(ds_data.GetColumn(0,"BHB"))) == 0) {
		gfn_showAlert("CW00001","발행구분");
		return false;
	}	
	if(Length(Trim(ds_data.GetColumn(0,"RIV"))) == 0) {
		gfn_showAlert("CW00001","계산서청구주기");
		return false;
	}		
	if(Length(Trim(ds_data.GetColumn(0,"RDT"))) == 0) {
		gfn_showAlert("CW00001","계산서청구일");
		return false;
	}	
	if(Length(Trim(ds_data.GetColumn(0,"JZR"))) == 0) {
		gfn_showAlert("CW00001","영세율적용정보");
		return false;
	}	
	if(Length(Trim(ds_data.GetColumn(0,"SZR"))) == 0) {
		gfn_showAlert("CW00001","영세율적용정보");
		return false;
	}
	if(Length(Trim(ds_data.GetColumn(0,"CHR"))) == 0) {
		gfn_showAlert("CW00001","계산서담당1");
		return false;
	}
	if(Lengthb(Trim(ds_data.GetColumn(0,"CHR"))) > 20) {
		gfn_showAlert("계산서담당1의 입력내용이 너무 깁니다.\n20 bytes 이하로 입력하십시요.");
		return false;
	} 

	if(chk_email.value == true && Length(Trim(ds_data.GetColumn(0,"EM1"))) == 0) {
		gfn_showAlert("CW00001","이메일1");
		return false;
	}

	if(chk_sms.value == true && Length(Trim(ds_data.GetColumn(0,"TEB"))) == 0) {
		gfn_showAlert("CW00001","SMS수신번호");
		return false;
	} 

	//========================계산서담당1 SMS수신번호 검증로직 시작====================
	else if(chk_sms.value == true && gfn_checkPhoneNumber(ds_data.GetColumn(0,"TEB"))) {
		gfn_showAlert("올바른 SMS수신번호를 입력해주십시오.");
		return false;
	} else if(chk_sms.value == false && Length(Trim(ds_data.GetColumn(0,"TEB"))) > 0) { // 체크박스가 비활성화 되어 있고 입력된 값이 있다면
		ds_data.SetColumn(0,"TEB", gfn_replaceSpecialChar(trim(replace(ds_data.GetColumn(0,"TEB"), " ", "")))); // 공백 및 특수문자를 제거하여 데이터셋 재세팅 함.
	}
	//=================================================================================

	if(chk_sm3.value == true && Length(Trim(ds_data.GetColumn(0,"TE3"))) == 0) {
		gfn_showAlert("CW00001","HRTS SMS수신번호");
		return false;
	}

	//===========================HRTS SMS수신번호 검증로직 시작=======================
	else if(chk_sm3.value == true && gfn_checkPhoneNumber(ds_data.GetColumn(0,"TE3"))) {
		gfn_showAlert("올바른 HRTS SMS수신번호를 입력해주십시오.");
		return false;
	} else if(chk_sm3.value == false && Length(Trim(ds_data.GetColumn(0,"TE3"))) > 0) { // 체크박스가 비활성화 되어 있고 입력된 값이 있다면
		ds_data.SetColumn(0,"TE3", gfn_replaceSpecialChar(trim(replace(ds_data.GetColumn(0,"TE3"), " ", "")))); // 공백 및 특수문자를 제거하여 데이터셋 재세팅 함.
	}
	//==================================================================================

	if(chk_sms.value == true && substr(Trim(ds_data.GetColumn(0,"TEB")),0,2) != "01") {
		gfn_showAlert("휴대폰번호를 입력하십시오.");
		return false;
	}
	/*if(chk_sms.value == true && (Length(Trim(ds_data.GetColumn(0,"TEB"))) > 11 || Length(Trim(ds_data.GetColumn(0,"TEB"))) < 10 )) {
		gfn_showAlert("전화번호 자릿수를 확인하십시오.");
		return false;
	}*/

	if(chk_sm3.value == true && substr(Trim(ds_data.GetColumn(0,"TE3")),0,2) != "01") {
		gfn_showAlert("HRTS 휴대폰번호를 입력하십시오.");
		return false;
	}
	/*if(chk_sm3.value == true && (Length(Trim(ds_data.GetColumn(0,"TE3"))) > 11 || Length(Trim(ds_data.GetColumn(0,"TE3"))) < 10 )) {
		gfn_showAlert("HRTS 전화번호 자릿수를 확인하십시오.");
		return false;
	}*/

	// 2020.12.09 자릿수 초과로 이메일 입력부분 유효성 조건 추가. Han J.H
	if(Lengthb(Trim(ds_data.GetColumn(0,"EM1"))) > 90) {
		gfn_showAlert("계산서담당1의 이메일1의 입력내용이 너무 깁니다.\n90 bytes 이하로 입력하십시요.");
		return false;	
	} else {
		if(Length(Trim(ds_data.GetColumn(0,"EM1"))) > 0) {
			if(gfn_isValidEmail(Trim(ds_data.GetColumn(0,"EM1"))) <> 1) {
				gfn_showAlert("CW00022",Static25.Text);
				return false;
			}
		}	
	}

	if(Lengthb(Trim(ds_data.GetColumn(0,"EM2"))) > 90) {
		gfn_showAlert("계산서담당1의 이메일2의 입력내용이 너무 깁니다.\n90 bytes 이하로 입력하십시요.");
		return false;	
	} else {
		if(Length(Trim(ds_data.GetColumn(0,"EM2"))) > 0) {
			if(gfn_isValidEmail(Trim(ds_data.GetColumn(0,"EM2"))) <> 1) {
				gfn_showAlert("CW00022",Static39.Text);
				return false;
			}
		}			
	}

	if(Lengthb(Trim(ds_data.GetColumn(0,"EM3"))) > 90) {
		gfn_showAlert("HRTS담당 이메일의 입력내용이 너무 깁니다.\n90 bytes 이하로 입력하십시요.");
		return false;	
	} else {	
		if(Length(Trim(ds_data.GetColumn(0,"EM3"))) > 0) {
			if(gfn_isValidEmail(Trim(ds_data.GetColumn(0,"EM3"))) <> 1) {
				gfn_showAlert("CW00022",Static49.Text);
				return false;
			}
		}
	}

	//====================담당자전화번호 검증로직 추가============================
	if(Length(Trim(ds_data.GetColumn(0,"TE2"))) > 0 && gfn_checkPhoneNumber(ds_data.GetColumn(0,"TE2")) == true ) {
		gfn_showAlert("올바른 담당자전화번호를 입력해주십시오.");
		return false;
	} else if(Length(Trim(ds_data.GetColumn(0,"TE2"))) > 0 && gfn_checkPhoneNumber(ds_data.GetColumn(0,"TE2")) == false ){
		ds_data.SetColumn(0,"TE2", gfn_replaceSpecialChar(trim(replace(ds_data.GetColumn(0,"TE2"), " ", ""))));
	}
	//============================================================================

	//====================현장전화번호 검증로직 추가==============================
	if(Length(Trim(ds_data.GetColumn(0,"TEC"))) > 0 && gfn_checkPhoneNumber(ds_data.GetColumn(0,"TEC")) == true ) {
		gfn_showAlert("올바른 현장전화번호를 입력해주십시오.");
		return false;		
	} else if(Length(Trim(ds_data.GetColumn(0,"TEC"))) > 0 && gfn_checkPhoneNumber(ds_data.GetColumn(0,"TEC")) == false ) {
		ds_data.SetColumn(0,"TEC", gfn_replaceSpecialChar(trim(replace(ds_data.GetColumn(0,"TEC"), " ", ""))));
	}
	//============================================================================

	//====================HRTS 전화번호1 검증로직 추가==============================
	if(Length(Trim(ds_data.GetColumn(0,"TE4"))) > 0 && gfn_checkPhoneNumber(ds_data.GetColumn(0,"TE4")) == true ) {
		gfn_showAlert("올바른 HRTS 전화번호1을 입력해주십시오.");
		return false;
	} else if(Length(Trim(ds_data.GetColumn(0,"TE4"))) > 0 && gfn_checkPhoneNumber(ds_data.GetColumn(0,"TE4")) == false ) {
		ds_data.SetColumn(0,"TE4", gfn_replaceSpecialChar(trim(replace(ds_data.GetColumn(0,"TE4"), " ", ""))));
	}
	//==============================================================================

	//====================HRTS 전화번호2 검증로직 추가==============================
	if(Length(Trim(ds_data.GetColumn(0,"TE5"))) > 0 && gfn_checkPhoneNumber(ds_data.GetColumn(0,"TE5")) == true ) {
		gfn_showAlert("올바른 HRTS 전화번호2를 입력해주십시오.");
		return false;
	} else if(Length(Trim(ds_data.GetColumn(0,"TE5"))) > 0 && gfn_checkPhoneNumber(ds_data.GetColumn(0,"TE5")) == false ) {
		ds_data.SetColumn(0,"TE5", gfn_replaceSpecialChar(trim(replace(ds_data.GetColumn(0,"TE5"), " ", ""))));
	}
	//==============================================================================

	//====================연대보증인 전화번호 검증로직 추가==============================
	if(Length(Trim(ds_data.GetColumn(0,"TED"))) > 0 && gfn_checkPhoneNumber(ds_data.GetColumn(0,"TED")) == true ) {
		gfn_showAlert("올바른 연대보증인 전화번호를 입력해주십시오.");
		return false;
	} else if(Length(Trim(ds_data.GetColumn(0,"TED"))) > 0 && gfn_checkPhoneNumber(ds_data.GetColumn(0,"TED")) == false ) {
		ds_data.SetColumn(0,"TED", gfn_replaceSpecialChar(trim(replace(ds_data.GetColumn(0,"TED"), " ", ""))));
	}
	//==============================================================================	

	//====================현장명 검증로직 추가(이동진 요청)============================
	if(Length(Trim(ds_data.GetColumn(0,"SPT"))) == 0) {
		gfn_showAlert("CW00001","현장명");
		ed_spt.SetFocus();
		return false;
	}
	//=================================================================================

	// 대표자명(WEB) 최대 14글자로 입력 제한되도록 Validation 검증로직 추가
	// 2019.06.21 Han J.H
	if(Length(Trim(ds_data.GetColumn(0,"EPNAME"))) > 14) {
		var arr = [st_epname.Value, "15글자"];
		gfn_showAlert("CW00133", arr);
		return false;
	}
//===========================================================================	
	//상호(WBS)  입력 제한  2020.10.15
	if(Length(Trim(ds_data.GetColumn(0,"EGNAME"))) > 30) {
		var arr = [st_egname.Value, "30글자"];
		gfn_showAlert("CW00133", arr);
		return false;
	}	

	//법적조치사항 입력제한 2020.10.16
	if(Length(Trim(ds_data.GetColumn(0,"JSH"))) > 150) {
		var arr = [Static35.Value, "150글자"];
		gfn_showAlert("CW00133", arr);
		return false;
	}		

	//메모 입력제한 2020.10.16
	if(Length(Trim(ds_data.GetColumn(0,"MEMO"))) > 500) {
		var arr = [st_memo.Value, "500글자"];
		gfn_showAlert("CW00133", arr);
		return false;
	}		
	//계산서담당1  입력제한 2020.10.16
	if(Length(Trim(ds_data.GetColumn(0,"CHR"))) > 14) {
		var arr = [Static24.Value, "14글자"];
		gfn_showAlert("CW00133", arr);
		return false;
	}
	//계산서담당2  입력제한 2020.10.16	
	if(Length(Trim(ds_data.GetColumn(0,"CH2"))) > 14) {
		var arr = [Static38.Value, "14글자"];
		gfn_showAlert("CW00133", arr);
		return false;
	}		

	//담당부서 입력제한 2020.10.16	
	if(Length(Trim(ds_data.GetColumn(0,"CDP"))) > 20) {
		var arr = [Static11.Value, "20글자"];
		gfn_showAlert("CW00133", arr);
		return false;
	}		

//============================================================================	

	// 2020.09.09 담당자전화번호(교) 한글입력 제한
	if(Length(Trim(ds_data.GetColumn(0,"GY2"))) > 0) {
		var gy2No = replace(trim(ds_data.GetColumn(0,"GY2")), "%", "");
		for( var i = 0; i < length( gy2No ); i++ ) {
			if ( lengthb(charAt( gy2No, i)) == 2 ) {
				gfn_showAlert("담당자전화번호 교)에는 한글입력이 제한됩니다.");
				Edit3.SetFocus();
				Edit3.Text = '';				
				return;
			}
		}		
	}

	// 2020.09.11 전화번호(교) 한글입력 제한
	if(Lengthb(Trim(ds_data.GetColumn(0,"GYA"))) > 12) {
		gfn_showAlert("전화번호 교)의 입력내용이 너무 깁니다.\n12 bytes 이하로 입력하십시요.");
		return false;
	} else {
		if(Length(Trim(ds_data.GetColumn(0,"GYA"))) > 0) {
			var gyaNo = replace(trim(ds_data.GetColumn(0,"GYA")), "%", "");
			for( var i = 0; i < length( gyaNo ); i++ ) {
				if ( lengthb(charAt( gyaNo, i)) == 2 ) {
					gfn_showAlert("전화번호 교)에는 한글입력이 제한됩니다.");
					Edit15.SetFocus();
					Edit15.Text = '';				
					return;
				}
			}		
		}		
	}

	// 2020.09.23 SMS수신번호 한글입력 제한
	if(Length(Trim(ds_data.GetColumn(0,"TEB"))) > 0) {
		var tebNo = replace(trim(ds_data.GetColumn(0,"TEB")), "%", "");
		for( var i = 0; i < length( tebNo ); i++ ) {
			if ( lengthb(charAt( tebNo, i)) == 2 ) {
				gfn_showAlert("SMS 수신번호 에는 한글입력이 제한됩니다.");
				Edit20.SetFocus();
				Edit20.Text = '';				
				return;
			}
		}		
	}

	// 2020.12.02 SMS수신번호(교) 한글입력 제한
	if(Length(Trim(ds_data.GetColumn(0,"GYB"))) > 0) {
		var gybNo = replace(trim(ds_data.GetColumn(0,"GYB")), "%", "");
		for( var i = 0; i < length( gybNo ); i++ ) {
			if ( lengthb(charAt( gybNo, i)) == 2 ) {
				gfn_showAlert("SMS수신번호 교)에는 한글입력이 제한됩니다.");
				Edit18.SetFocus();
				Edit18.Text = '';				
				return;
			}
		}		
	}

	return true;
}

