function fn_repChar() {
	ed_nam.Text = replace(replace(replace(ed_nam.Text,chr(13)+chr(10),''),chr(13),''),chr(10),'');                 // 고객명
	ed_spt.Text = replace(replace(replace(ed_spt.Text,chr(13)+chr(10),''),chr(13),''),chr(10),'');                 // 현장명
	ed_egname.Text = replace(replace(replace(ed_egname.Text,chr(13)+chr(10),''),chr(13),''),chr(10),'');           // 상호(WEB)
	ed_epname.Text = replace(replace(replace(ed_epname.Text,chr(13)+chr(10),''),chr(13),''),chr(10),'');           // 대표자명(WEB)
	med_zipcode1.Text = replace(replace(replace(med_zipcode1.Text,chr(13)+chr(10),''),chr(13),''),chr(10),'');     // 세금계산서 수신처주소 우편번호
	ed_addr1.Text = replace(replace(replace(ed_addr1.Text,chr(13)+chr(10),''),chr(13),''),chr(10),'');             // 세금계산서 수신처주소1
	ed_addrDetail1.Text = replace(replace(replace(ed_addrDetail1.Text,chr(13)+chr(10),''),chr(13),''),chr(10),''); // 세금계산서 수신처주소2
	med_zipcode1_road.Text = replace(replace(replace(med_zipcode1_road.Text,chr(13)+chr(10),''),chr(13),''),chr(10),'');     // 세금계산서 수신처주소 우편번호 - 도로명
	ed_addr1_road.Text = replace(replace(replace(ed_addr1_road.Text,chr(13)+chr(10),''),chr(13),''),chr(10),'');             // 세금계산서 수신처주소1 - 도로명
	ed_addrDetail1_road.Text = replace(replace(replace(ed_addrDetail1_road.Text,chr(13)+chr(10),''),chr(13),''),chr(10),''); // 세금계산서 수신처주소2 - 도로명
	Edit12.Text = replace(replace(replace(Edit12.Text,chr(13)+chr(10),''),chr(13),''),chr(10),'');                 // 상호
	Edit13.Text = replace(replace(replace(Edit13.Text,chr(13)+chr(10),''),chr(13),''),chr(10),'');                 // 담당부서

	Edit17.Text = replace(replace(replace(Edit17.Text,chr(13)+chr(10),''),chr(13),''),chr(10),'');                 // 계산서담당1
	Edit2.Text = replace(replace(replace(Edit2.Text,chr(13)+chr(10),''),chr(13),''),chr(10),'');                   // 계산서담당2
	Edit20.Text = replace(replace(replace(Edit20.Text,chr(13)+chr(10),''),chr(13),''),chr(10),'');                 // SMS수신번호
	Edit6.Text = replace(replace(replace(Edit6.Text,chr(13)+chr(10),''),chr(13),''),chr(10),'');                   // 담당자전화번호
	Edit19.Text = replace(replace(replace(Edit19.Text,chr(13)+chr(10),''),chr(13),''),chr(10),'');                 // 이메일1
	Edit5.Text = replace(replace(replace(Edit5.Text,chr(13)+chr(10),''),chr(13),''),chr(10),'');                   // 이메일2	

	med_zipcode0.Text = replace(replace(replace(med_zipcode1.Text,chr(13)+chr(10),''),chr(13),''),chr(10),'');	   // 사업장주소 우편번호
	ed_addr0.Text = replace(replace(replace(ed_addr0.Text,chr(13)+chr(10),''),chr(13),''),chr(10),'');             // 사업장주소(WEB)1
	ed_addrDetail0.Text = replace(replace(replace(ed_addrDetail0.Text,chr(13)+chr(10),''),chr(13),''),chr(10),''); // 사업장주소(WEB)2

	med_zipcode1.Text      = replace(replace(med_zipcode1.Text,'_',''),'-','');
	med_zipcode1_road.Text = replace(replace(med_zipcode1_road.Text,'_',''),'-','');
	ed_nzib.Text           = replace(replace(ed_nzib.Text,'_',''),'-','');
	txt_jsh.Text           = replace(replace(replace(txt_jsh.Text,chr(13)+chr(10),''),chr(13),''),chr(10),'');   // 법적 조치사항 
	txt_memo.Text          = replace(replace(replace(txt_memo.Text,chr(13)+chr(10),''),chr(13),''),chr(10),'');   // 메모

}

function cb_zterm_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	//수금기준 보수이체 선택시 자동이체 Y
//	trace("cb_zterm_OnChanged(obj, " + strCode + ", " + strText + ", " + nOldIndex + ", " + nNewIndex + ")" );
	if(nNewIndex >= 4) {
		rd_iche.value = "Y";
	} else {
		rd_iche.value = "N";
	}

}
/********************************************************************************
* 기      능   : 주요 항목 변경 여부 체크 
********************************************************************************/
function fn_mdfLog(){
	 // 1. 상호 web
	 // 2. 대표자명 web
	 // 3. 지로수신처 주소 - 지번 우편번호
	 // 4. 지로수신처 주소 - 지번 주소1
	 // 5. 지로수신처 주소 - 지번 주소2
	 // 6. 지로수신처 주소 - 도로명 우편번호
	 // 7. 지로수신처 주소 - 도로명 주소1
	 // 8. 지로수신처 주소 - 도로명 주소2 
	 // 9. 지로수신처 주소 - 도로명 신규 우편번호 5 자리
	 // 10. 상호
	 // 11. 담당부서
	 // 12. 전화번호
	 // 13. 역발행여부
	 // 14. 계산서수신구분
	 // 15. 이메일1
	 // 16. 정기보수 영세율 CS121_JZR 
	var log1_o,log2_o,log3_o,log4_o,log5_o,log6_o,log7_o,log8_o,log9_o,log10_o,log11_o,log12_o,log13_o,log14_o,log15_o;
	var log1_n,log2_n,log3_n,log4_n,log5_n,log6_n,log7_n,log8_n,log9_n,log10_n,log11_n,log12_n,log13_n,log14_n,log15_n;
	var log1_t,log2_t,log3_t,log4_t,log5_t,log6_t,log7_t,log8_t,log9_t,log10_t,log11_t,log12_t,log13_t,log14_t,log15_t;

	var log16_o;
	var log16_n;
	var log16_t;

	ds_log.ClearData();

	log1_o = ds_data.GetOrgColumn(0, "EGNAME");
	log1_n = ds_data.GetColumn(0, "EGNAME");
	log1_t = "EGNAME";
	fn_setLogDs(log1_o,log1_n,log1_t);

	log2_o = ds_data.GetOrgColumn(0, "EPNAME");
	log2_n = ds_data.GetColumn(0, "EPNAME");
	log2_t = "EPNAME";
	fn_setLogDs(log2_o,log2_n,log2_t);

	log3_o = ds_data.GetOrgColumn(0, "ZIB");
	log3_n = ds_data.GetColumn(0, "ZIB");
	log3_t = "CS121_ZIB";
	fn_setLogDs(log3_o,log3_n,log3_t);

	log4_o = ds_data.GetOrgColumn(0, "P1B");
	log4_n = ds_data.GetColumn(0, "P1B");
	log4_t = "CS121_P1B";
	fn_setLogDs(log4_o,log4_n,log4_t);

	log5_o = ds_data.GetOrgColumn(0, "P2B");
	log5_n = ds_data.GetColumn(0, "P2B");
	log5_t = "CS121_P2B";
	fn_setLogDs(log5_o,log5_n,log5_t);

	log6_o = ds_data.GetOrgColumn(0, "DZIB");
	log6_n = ds_data.GetColumn(0, "DZIB");
	log6_t = "CS121_DZIB";
	fn_setLogDs(log6_o,log6_n,log6_t);

	log7_o = ds_data.GetOrgColumn(0, "DP1B");
	log7_n = ds_data.GetColumn(0, "DP1B");
	log7_t = "CS121_DP1B";
	fn_setLogDs(log7_o,log7_n,log7_t);

	log8_o = ds_data.GetOrgColumn(0, "DP2B");
	log8_n = ds_data.GetColumn(0, "DP2B");
	log8_t = "CS121_DP2B";
	fn_setLogDs(log8_o,log8_n,log8_t);

	log9_o = ds_data.GetOrgColumn(0, "NZIB");
	log9_n = ds_data.GetColumn(0, "NZIB");
	log9_t = "CS121_NZIB";
	fn_setLogDs(log9_o,log9_n,log9_t);	

	log10_o = ds_data.GetOrgColumn(0, "GSH");
	log10_n = ds_data.GetColumn(0, "GSH");
	log10_t = "CS121_GSH";
	fn_setLogDs(log10_o,log10_n,log10_t);

	log11_o = ds_data.GetOrgColumn(0, "CDP");
	log11_n = ds_data.GetColumn(0, "CDP");
	log11_t = "CS121_CDP";
	fn_setLogDs(log11_o,log11_n,log11_t);

	log12_o = ds_data.GetOrgColumn(0, "TEA");
	log12_n = ds_data.GetColumn(0, "TEA");
	log12_t = "CS121_TEA";
	fn_setLogDs(log12_o,log12_n,log12_t);

	log13_o = ds_data.GetOrgColumn(0, "RSN");
	log13_n = ds_data.GetColumn(0, "RSN");
	log13_t = "CS121_RSN";
	fn_setLogDs(log13_o,log13_n,log13_t);

	log14_o = ds_data.GetOrgColumn(0, "GBN");
	log14_n = ds_data.GetColumn(0, "GBN");
	log14_t = "CS121_GBN";
	fn_setLogDs(log14_o,log14_n,log14_t);

	log15_o = ds_data.GetOrgColumn(0, "EM1");
	log15_n = ds_data.GetColumn(0, "EM1");
	log15_t = "CS121_EM1";
	fn_setLogDs(log15_o,log15_n,log15_t);

	log16_o = ds_data.GetOrgColumn(0, "JZR");
	log16_n = ds_data.GetColumn(0, "JZR");
	log16_t = "CS121_JZR";
	fn_setLogDs(log16_o,log16_n,log16_t);


}
/********************************************************************************
* 기      능   : 주요 항목 변경 여부 체크 
********************************************************************************/
function fn_setLogDs(o,n,t) {

	var val_o = o;
	var val_n = n;
	var val_t = t;

	if(o <> n){
		 var rows = ds_log.AddRow();
		 ds_log.SetColumn(rows, "OLD", o);
		 ds_log.SetColumn(rows, "NEW", n);
		 ds_log.SetColumn(rows, "COL", t);
		 ds_log.SetColumn(rows, "UPN", ds_data.GetColumn(0,"UPN"));
		 ds_log.SetColumn(rows, "CST", ds_data.GetColumn(0,"CST"));
	}

}
