function btn_detailLog_OnClick(obj)
{
	if(ds_logList.RowCount() <> 0) {
		gfn_openPopup("CS1601001C",true);
	} else {
		return;
	}	
}
function fn_saveLog() {

	if(ds_log.RowCount() <> 0) {
		tit_clearActionInfo();
		tit_addMultiActionInfo("cs16:CS1601001A_I4");
		tit_callService(
			  ""
			, ""
			, "ds_list=ds_log"				
			, ""							
			, ""							
			, ""							
			, true							
			, false							
			, true); 	
	}

	// 유효성 검증 실패시 무조건 조회되지 않도록 보완. 2019.02.27 Han J.H
	fn_query2();
}

/********************************************************************************
* 기      능   : 전화번호 유효성 체크
********************************************************************************/
function gfn_checkPhoneNumber(str) {
	var rStr = trim(str);
	var rLength = length(rStr);
	var rReutrnValue = false;

	// 특수문자 존재여부 체크
	if(gfn_isSpecialStr(rStr)) {
		gfn_showAlert("입력된 전화번호 문자열에 특수문자가 포함되어 있습니다.");
		rReutrnValue = true;
	}

	// 자릿수 체크(9자리 미만이거나 11자리 초과일때 false)
	if(rLength < 9 || rLength > 11) {
		gfn_showAlert("입력된 전화번호 자릿수가 9자리 미만이거나 11자리 초과입니다.");
		rReutrnValue = true;
	}

	return rReutrnValue;
}
